sakstype(Personlig)
    .kontekst(KontrollSaksbehandler, ValideringSkattepliktig) {
        skattemelding utslagFor hvis(
            aksjesparekonto hvor (
                (
                    (
                        (
                            grunnlag(verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall) +
                                grunnlag(formuesverdiForKontanter.beloepSomTall)
                            ) -
                            (
                                verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall +
                                    formuesverdiForKontanter.beloepSomTall
                                )
                        ) erStørreEnnEllerLik 100_000_000
                    )
                    eller (
                        (
                            (
                                verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall +
                                    formuesverdiForKontanter.beloepSomTall
                                ) -
                                (
                                    grunnlag(verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall) +
                                        grunnlag(formuesverdiForKontanter.beloepSomTall)
                                    )
                            ) erStørreEnnEllerLik 100_000_000
                        )
                )
        ) {
            betjeningsstrategiSettesTil(
                myndighetsfastsettingMedVarselOgVedtak,
                merknadStandard,
                1,
                Gruppering settesTil Finans,
                spesialomraade
            )
        }
    }

