kontrollformel:
sakstype(Personlig)
    .kontekst(AdHocLabel) {
        // Gir utslag dersom oppgitt formue på aksjer i AR er ulik grunnlag med 50 000 eller mer,
        // eller fastatt utbytte, gevinst eller tap på aksjer i AR er ulik grunnlag med 300 eller mer
        // kontrollen gir kun utvidet veiledning (må allikevel ha veilednings betjeningsstrategi i SMIA, men uten handlingsplan)
        skattemelding
            .forForekomsterAv(aksjeIAksjonaerregisteret)
            .utslagFor {
                val differanseUtbytte = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigUtbytte.beloepSomTall) er oppgitt) og
                        (
                            differanse(
                                fastsatt(skattepliktigUtbytte.beloepSomTall),
                                grunnlag(skattepliktigUtbytte.beloepSomTall)
                            ) erStørreEnn 300
                            )
                }

                val differansenGevinst = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) er oppgitt) og
                        (
                            differanse(
                                fastsatt(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall),
                                grunnlag(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall)
                            ) erStørreEnn 300
                            )
                }

                val differanseTap = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall) er oppgitt) og
                        (
                            differanse(
                                fastsatt(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall),
                                grunnlag(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall)
                            ) erStørreEnn 300
                            )
                }

                val differanseFormue = hvorMinstEnForekomst {
                    (grunnlag(verdiFoerVerdsettingsrabattForAksje.beloepSomTall) er oppgitt) og
                        (
                            differanse(
                                fastsatt(verdiFoerVerdsettingsrabattForAksje.beloepSomTall),
                                grunnlag(verdiFoerVerdsettingsrabattForAksje.beloepSomTall)
                            ) erStørreEnn 50_000
                            )
                }

                hvis(differanseUtbytte eller differansenGevinst eller differanseTap eller differanseFormue) {
                    betjeningsstrategiSettesTil(
                        sendInfoOmUtvidetVeiledning,
                        visUtvidetVeiledning,
                        1,
                        Gruppering settesTil Finans,
                        spesialomraade,
                        ikkeObligatorisk,
                        FokusDokument settesLik grunnlag(skattemelding)
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        skattemelding utslagFor hvis(
            (
                arbeidsgiver hvorMinstEnForekomst (
                    samledeYtelserFraArbeidsgiverPerBehandlingsart
                        hvorMinstEnForekomst (beloepSomGruppe.metodeVedDobbeltbeskatning erLik "denAlternativeFordelingsmetoden")
                    )
                )
                eller (
                    pensjonsinntekt hvorMinstEnForekomst (
                        perMaaned hvorMinstEnForekomst (
                            pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning erLik "denAlternativeFordelingsmetoden"
                            )
                        )
                    )
        ) {
            hvis(ikke(tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden er oppgitt)) {
                betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
            }
        }
    }

