sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har redusert annen skattepliktig kapitalinntekt på annet finansprodukt
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(annetFinansprodukt)
            .filtrer { grunnlag(annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepSomTall) erStørreEnn 0 }
            .utslagFor {
                val differanseGevinst =
                    (
                        grunnlag(annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepSomTall) - fastsatt(
                            annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepSomTall
                        )
                        )
                val harVedlegg =
                    annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.beloepIValuta har vedlegg

                hvis(ikke(harVedlegg) og (differanseGevinst erStørreEnnEllerLik 50_000)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadDokumentasjon,
                        1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har egenfastsatt tap på annet finansprodukt
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(annetFinansprodukt)
            .forForekomsterAv(
                sti(
                    skattepliktigTapVedRealisasjonAvAnnetFinansprodukt,
                    beloepUtenHensynTilValgtPrioritertFradragstype
                )
            )
            .filtrer { grunnlag(beloepSomTall) erLik 0 }
            .utslagFor {
                val harVedlegg = beloepIValuta har vedlegg
                val differanseTap = (fastsatt(beloepSomTall) - grunnlag(beloepSomTall))

                // skal kun gi merknad i SME fra 50 000 til 5 mill (for å unngå flere merknader på samme beløp)
                hvis(ikke(harVedlegg) og (differanseTap erStørreEnnEllerLik 50_000) og (differanseTap erMindreEnn 5_000_000)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadDokumentasjon,
                        1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { fastsattEllerGrunnlag(partsnummer) }

        // AB-test: kontrollen gir utslag dersom skattyter har redusert skattepliktig gevinst på annet finansprodukt
        // kontrollen gir utslag på skattytere med 0,2,4,6 eller 8 i 7.siffer i partsnummeret
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(annetFinansprodukt)
            .filtrer { grunnlag(skattepliktigGevinstVedRealisasjonAvAnnetFinansprodukt.beloepSomTall) erStørreEnn 0 }
            .utslagFor {
                val differanseGevinst =
                    (
                        grunnlag(skattepliktigGevinstVedRealisasjonAvAnnetFinansprodukt.beloepSomTall) - fastsatt(
                            skattepliktigGevinstVedRealisasjonAvAnnetFinansprodukt.beloepSomTall
                        )
                        )
                val harVedlegg =
                    skattepliktigGevinstVedRealisasjonAvAnnetFinansprodukt.beloepIValuta har vedlegg

                // skal kun gi merknad i SME fra 50 000 til 5 mill (for å unngå flere merknader på samme beløp)
                hvis(
                    ikke(harVedlegg) og (partsnr.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8")) og
                        ((differanseGevinst erStørreEnnEllerLik 50_000) og (differanseGevinst erMindreEnn 5_000_000))
                ) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadDokumentasjon,
                        1
                    )
                }
            }
    }

