sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {
        val felter = listeAv(
            beloepSomGruppe.metodeVedDobbeltbeskatning,
            pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning
        )

        // Kontrollen gir utslag og merknad dersom skattyter har oppgitt tilleggsinformasjon for den alternative fordelingsmetoden
        // og IKKE har oppgitt metode for dobbelbeskatning = den alternative fordelingsmetoden på lønn eller pensjon
        // merknaden skal kun vises på tilleggsinformasjonskortet og ikke på lønn og pensjons kortene.
        skattemelding
            .utslagFor {
                // har tilleggsinformasjon om den alternative fordelingsmetoden
                val harKravOmDenAlternativeFordelingsmetoden =
                    forForekomsterAv(
                        tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden
                    )
                        .hvorMinstEnForekomst {
                            (startdatoForOppholdIUtland er oppgitt) eller
                                (sluttdatoForOppholdIUtland er oppgitt)
                        }
                // lønns- eller pensjonskort som ikke har metode for dobbelbeskatning = den alternative fordelingsmetoden
                val kortUtenMetodeVedDobbeltbeskatningLikDenAlternativeFordelingsmetoden = forForekomsterAv(
                    gruppe(
                        arbeidsgiver,
                        pensjonsinntekt
                    )
                ).harIngenForekomsterDer {
                    val harMinstEttFeltHvorMetodeVedDobbeltbeskatningLikDenAlternativeFordelingsmetoden = (
                        forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart) +
                            forForekomsterAv(perMaaned)
                        )
                        .hvorMinstEnForekomst {
                            felter
                                .map { felt -> felt erLik "denAlternativeFordelingsmetoden" }
                                .reduce { venstreBetingelse, hoeyreBetingelse ->
                                    venstreBetingelse eller hoeyreBetingelse
                                }
                        }

                    harMinstEttFeltHvorMetodeVedDobbeltbeskatningLikDenAlternativeFordelingsmetoden
                }

                // For å forhindre at alle forekomstene til gruppene over blir med i utslaget
                val antallKortUtenMetodeVedDobbeltbeskatningLikDenAlternativeFordelingsmetoden =
                    KonstantTallFelt<Rot>(
                        utslagFor {
                            hvis(kortUtenMetodeVedDobbeltbeskatningLikDenAlternativeFordelingsmetoden) {
                                betjeningsstrategiSettesTil(
                                    ikke,
                                    ingen
                                )
                            }
                        }
                            .filter { it.resultat }.size.toLong()
                    )

                hvis(
                    harKravOmDenAlternativeFordelingsmetoden og (antallKortUtenMetodeVedDobbeltbeskatningLikDenAlternativeFordelingsmetoden erStørreEnn 0)
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {
        val felter = listeAv(
            beloepSomGruppe.metodeVedDobbeltbeskatning,
            pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning
        )

        // kontrollen gir utslag og merknad dersom skattyter har oppgitt tilleggsinformasjon for skattenedsettelse etter ettårsregelen
        // og IKKE har oppgitt metode for dobbelbeskatning = ettårsregelen på lønn eller pensjon
        // merknaden skal kun vises på tilleggsinformasjonskortet og ikke på lønn og pensjons kortene.
        skattemelding
            .utslagFor {
                // har tilleggsinformasjon om skattenedsettelse etter ettårsregelen
                val harKravOmSKattenedsettelseEtterEttaarsregelen =
                    forForekomsterAv(tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen)
                        .hvorMinstEnForekomst {
                            (startdatoForArbeidsoppholdIUtland er oppgitt) eller
                                (sluttdatoForArbeidsoppholdIUtland er oppgitt) eller
                                (antallOppholdsdagerINorge er oppgitt)
                        }
                // lønns- eller pensjonskort som ikke har metode for dobbelbeskatning = ettårsregelen
                val kortUtenMetodeVedDobbeltbeskatningLikEttaarsregel = forForekomsterAv(
                    gruppe(
                        arbeidsgiver,
                        pensjonsinntekt
                    )
                ).harIngenForekomsterDer {
                    val harMinstEttFeltHvorMetodeVedDobbeltbeskatningLikEttaarsregel = (
                        forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart) +
                            forForekomsterAv(perMaaned)
                        )
                        .hvorMinstEnForekomst {
                            felter
                                .map { felt -> felt erLik "ettaarsregelen" }
                                .reduce { venstreBetingelse, hoeyreBetingelse ->
                                    venstreBetingelse eller hoeyreBetingelse
                                }
                        }

                    harMinstEttFeltHvorMetodeVedDobbeltbeskatningLikEttaarsregel
                }

                // For å forhindre at alle forekomstene til gruppene over blir med i utslaget
                val antallKortUtenMetodeVedDobbeltbeskatningLikEttaarsregel =
                    KonstantTallFelt<Rot>(
                        utslagFor {
                            hvis(kortUtenMetodeVedDobbeltbeskatningLikEttaarsregel) {
                                betjeningsstrategiSettesTil(
                                    ikke,
                                    ingen
                                )
                            }
                        }
                            .filter { it.resultat }.size.toLong()
                    )

                hvis(
                    harKravOmSKattenedsettelseEtterEttaarsregelen og (antallKortUtenMetodeVedDobbeltbeskatningLikEttaarsregel erStørreEnn 0)
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1)
                }
            }
    }

