                }
            }
    }

sakstype(Personlig)
       .kontekst(AdHocLabel)
    {
    // Gir utslag dersom oppgitt formue på aksjer i AR er ulik grunnlag med 100 000 eller mer,
    // eller fastatt utbytte, gevinst eller tap på aksjer i AR er ulik grunnlag med 15 000 eller mer
    // kontrollen gir veiledningsbrev og utvidet veiledning
    skattemelding
        .deaktivertKontrollAldriUtslagFor {
            val differanseFormue = differanse(
                fastsatt(verdiFoerVerdsettingsrabattForAksje.beloepSomTall),
                grunnlag(verdiFoerVerdsettingsrabattForAksje.beloepSomTall))

            val differanseUtbytte = differanse(
                fastsatt(skattepliktigUtbytte.beloepSomTall),
                grunnlag(skattepliktigUtbytte.beloepSomTall))

            val differanseGevinst = differanse(
                fastsatt(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall),
                grunnlag(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall))

            val differanseTap = differanse(
                fastsatt(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall),
                grunnlag(skattepliktigTapVedRealisasjonAvAksje.fradragsberettigetBeloep.beloepSomTall))

            hvis((differanseFormue erStørreEnnEllerLik 100_000) eller
                (differanseUtbytte erStørreEnnEllerLik 15_000) eller
                (differanseGevinst erStørreEnnEllerLik 15_000) eller
                (differanseTap erStørreEnnEllerLik 15_000)) {
                betjeningsstrategiSettesTil(
                    sendInfoOmUtvidetVeiledning, visUtvidetVeiledning, 1,
                    Gruppering settesLik Finans,
                    spesialomraade
                )
            }
        }
    }

