        skattemelding
            .utslagFor {
                val kortMedEndringsaarsak = forAlleForekomster
                    .hvorMinstEnForekomst { aarsak erLik "bosattEtterSkatteavtaleIAnnetLand" }

                val harOppgittTilleggsinformasjonForBosattEtterAvtale = skattemelding
                    .hvorMinstEnForekomst { tilleggsinformasjonVedSkattemessigBosattIUtlandEtterSkatteavtale er oppgitt }

                hvis(
                    harBegrensetSkatteplikt og
                        (kortMedEndringsaarsak eller harOppgittTilleggsinformasjonForBosattEtterAvtale)
