sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {
        // kontrollen gir utslag og merknad dersom skattyter har oppgitt betalt skatt i utlandet
        // og det ikke er oppgitt noe annet kort med samme land og dobbeltbeskatningsmetode
        // NB: Har hoppet over sjekk på feltene for eiendom siden land ligger på fastEiendom
        // TODO: Må skrive om denne når vi får på plass funksjonalitet for å sette merkanden der vi ønsker det
        val felter = listeAv(
            beloepSomGruppe.metodeVedDobbeltbeskatning,
            pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning,
            innskudd.metodeVedDobbeltbeskatning,
            opptjenteRenter.metodeVedDobbeltbeskatning,
            tilbakefoertRente.metodeVedDobbeltbeskatning,
            gevinstValutalaan.metodeVedDobbeltbeskatning,
            verdiFoerVerdsettingsrabattForAksje.metodeVedDobbeltbeskatning,
            skattepliktigUtbytte.metodeVedDobbeltbeskatning,
            skattepliktigGevinstVedRealisasjonAvAksje.metodeVedDobbeltbeskatning,
            verdiFoerVerdsettingsrabattForAksje.metodeVedDobbeltbeskatning,
            skattepliktigUtbytte.metodeVedDobbeltbeskatning,
            skattepliktigGevinstVedRealisasjonAvAksje.metodeVedDobbeltbeskatning,
            skattepliktigUtbytte.metodeVedDobbeltbeskatning,
            skattepliktigRenteinntekt.metodeVedDobbeltbeskatning,
            verdiFoerVerdsettingsrabattForAndelIAksjedel.metodeVedDobbeltbeskatning,
            skattepliktigFormuesverdiForAndelIRentedel.metodeVedDobbeltbeskatning,
            skattepliktigGevinstVedRealisasjonAvAndelIAksjedel.metodeVedDobbeltbeskatning,
            skattepliktigGevinstVedRealisasjonAvAndelIRentedel.metodeVedDobbeltbeskatning,
            formuesverdiForObligasjonOgSertifikat.metodeVedDobbeltbeskatning,
            renteinntektAvObligasjonOgSertifikat.metodeVedDobbeltbeskatning,
            skattepliktigGevinstVedRealisasjonAvObligasjonOgSertifikat.metodeVedDobbeltbeskatning,
            formuesverdiForKontanter.metodeVedDobbeltbeskatning,
            verdiFoerVerdsettingsrabattForAksjedel.metodeVedDobbeltbeskatning,
            skattepliktigGevinstVedRealisasjon.metodeVedDobbeltbeskatning,
            formuesverdiForAnnetFinansprodukt.metodeVedDobbeltbeskatning,
            skattepliktigGevinstVedRealisasjonAvAnnetFinansprodukt.metodeVedDobbeltbeskatning,
            renteinntekt.metodeVedDobbeltbeskatning,
            annenSkattepliktigKapitalinntektFraAnnetFinansprodukt.metodeVedDobbeltbeskatning,
            innbetalingTilIndividuellPensjonsordningPerSkattleggingsperiode.metodeVedDobbeltbeskatning,
            utbetalingFraIndividuellPensjonsordningPerSkattleggingsperiode.metodeVedDobbeltbeskatning,
            // gevinstVedRealisasjonAvFastEiendom.metodeVedDobbeltbeskatning,
            // bruttoLeieinntektVedSjablongbehandletUtleie.metodeVedDobbeltbeskatning,
            // bruttoLeieinntekt.metodeVedDobbeltbeskatning,
            inntektSomGruppe.metodeVedDobbeltbeskatning,
            formuesverdi.metodeVedDobbeltbeskatning
        )

        val kort = skattemelding
            .forForekomsterAv(
                gruppe(
                    arbeidsgiver,
                    pensjonsinntekt,
                    konto,
                    aksjeIkkeIAksjonaerregisteret,
                    aksjeIAksjonaerregisteret,
                    verdipapirfond,
                    obligasjonOgSertifikat,
                    aksjesparekonto,
                    annetFinansprodukt,
                    skattefavorisertIndividuellSparingTilPensjon,
                    // realisasjonAvFastEiendom,
                    // sjablongbehandletUtleieIUtlandet,
                    // regnskapsbehandletUtleieIUtlandet,
                    annenKapitalinntekt
                    // formuesgrunnlagSelveidBolig,
                    // formuesgrunnlagBoenhetIBoligselskap,
                    // formuesgrunnlagFlerboligbygning,
                    // formuesgrunnlagTomt,
                    // formuesgrunnlagAnnenFastEiendomInnenforInntektsgivendeAktivitet,
                    // formuesgrunnlagAnnenFastEiendomUtenforInntektsgivendeAktivitet,
                    // formuesgrunnlagBorett,
                    // formuesgrunnlagEgenFritaksbehandletBolig,
                    // formuesgrunnlagRegnskapsbehandletBolig,
                    // formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                    // formuesgrunnlagRegnskapsbehandletFritidseiendom,
                    // formuesgrunnlagSelveidFritidseiendom
                )
            )

        val eiendommerIUtlandet = skattemelding
            .forForekomsterAv(fastEiendom)
            .filtrer { (landkode er oppgitt) og (landkode erUlik "NO") }

        skattemelding
            .forForekomsterAv(kravOmKreditfradragForUtenlandskSkatt)
            .utslagFor {
                val landMedUtenlandskSkatt = verdiFor { landkode }
                val harKravOmKreditfradragForUtenlandskSkatt = hvorMinstEnForekomst {
                    (kravOmKreditfradragForUtenlandskFormuesskatt.beloepSomTall erStørreEnn 0) eller
                        (kravOmKreditfradragForUtenlandskInntektsskatt.beloepSomTall erStørreEnn 0) eller
                        (skattBetaltPaaAksjeutbytteMottattFraUtenlandskSelskap.beloepSomTall erStørreEnn 0) eller
                        (bruttoAksjeutbytteMottattFraUtenlandskSelskap.beloepSomTall erStørreEnn 0)
                }

                val harEiendomISammeLandSomKravOmKredit = eiendommerIUtlandet
                    .hvorMinstEnForekomst { landkode erLik landMedUtenlandskSkatt }

                val kortMedLandOgFeltMedMetodeVedDobbeltbeskatningOppgitt = kort
                    .hvorMinstEnForekomst {
                        val harMinstEttFeltHvorMetodeVedDobbeltbeskatningErOppgitt = (
                            this +
                                forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart) +
                                forForekomsterAv(perMaaned)
                            )
                            .hvorMinstEnForekomst {
                                // Her står det egentlig
                                // (beloepSomGruppe.metodeVedDobbeltbeskatning er oppgitt) eller
                                // (pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning) eller .....
                                felter
                                    .map { felt -> felt erLik "kredit" }
                                    .reduce { venstreBetingelse, hoeyreBetingelse ->
                                        venstreBetingelse eller hoeyreBetingelse
                                    }
                            }

                        (landkode erLik landMedUtenlandskSkatt) og harMinstEttFeltHvorMetodeVedDobbeltbeskatningErOppgitt
                    }

                // For å forhindre at alle forekomstene til gruppene over blir med i utslaget
                val antallKortMedLandOgFeltMedMetodeVedDobbeltbeskatningOppgitt =
                    KonstantTallFelt<Rot>(
                        utslagFor {
                            hvis(kortMedLandOgFeltMedMetodeVedDobbeltbeskatningOppgitt) {
                                betjeningsstrategiSettesTil(
                                    ikke,
                                    ingen
                                )
                            }
                        }
                            .filter { it.resultat }.size.toLong()
                    )

                hvis(
                    ikke(harEiendomISammeLandSomKravOmKredit) og harKravOmKreditfradragForUtenlandskSkatt og (antallKortMedLandOgFeltMedMetodeVedDobbeltbeskatningOppgitt erLik 0)
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1)
                }
            }
    }

