sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val boligForekomsterSekundaer = skattemelding
            .forForekomsterAv(
                gruppe(
                    formuesgrunnlagSelveidBolig,
                    formuesgrunnlagBoenhetIBoligselskap,
                    formuesgrunnlagBorett
                )
            )
            .filtrer { (grunnlag(boligbruk) erLik "sekundaerbolig") og (grunnlag(formuesverdi.beloepSomTall) er oppgitt) }
            .verdierFor { grunnlag(internEiendomsidentifikator) }

        val boligForekomsterFlerboligSekundaer = skattemelding
            .forForekomsterAv(formuesgrunnlagFlerboligbygning)
            .filtrer { grunnlag(formuesverdi.beloepSomTall) er oppgitt }
            .forForekomsterAv(useksjonertBoenhet)
            .filtrer { grunnlag(boligbruk) erLik "sekundaerbolig" }
            .verdierFor { grunnlag(internEiendomsidentifikator) }

        val fritidseiendomForekomster = skattemelding
            .forForekomsterAv(
                gruppe(
                    formuesgrunnlagEgenFritaksbehandletFritidseiendom,
                    formuesgrunnlagRegnskapsbehandletFritidseiendom,
                    formuesgrunnlagSelveidFritidseiendom
                )
            )
            .filtrer { grunnlag(formuesverdi.beloepSomTall) ikkeEr oppgitt }
            .verdierFor { fastsatt(internEiendomsidentifikator) }

        val eiendommer = skattemelding.forForekomsterAv(fastEiendom)

        val fritidseiendomForekomsterSergID = eiendommer
            .filtrer { fastsatt(internEiendomsidentifikator).finnesBlant(fritidseiendomForekomster) }
            .verdierFor { SERGEiendomsidentifikator }

        val boligForekomsterSergID = eiendommer
            .filtrer {
                (grunnlag(internEiendomsidentifikator).finnesBlant(boligForekomsterSekundaer)) eller (
                    grunnlag(
                        internEiendomsidentifikator
                    ).finnesBlant(boligForekomsterFlerboligSekundaer)
                    )
            }
            .hvorMinstEnForekomst {
                (grunnlag(SERGEiendomsidentifikator) er oppgitt) og
                    (grunnlag(SERGEiendomsidentifikator) finnesBlant (fritidseiendomForekomsterSergID))
            }

        // kontrollen gir utslag på alle eiendommer hvor skattyter har endret eiendomstype fra bolig til fritidsbolig
        // skal kun gi utslag dersom formuesverdi er preutfylt på eiendommen
        // den gir både merknad i SME og oppgave i SMIA
        skattemelding
            .utslagFor {
                hvis(boligForekomsterSergID) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1
                    )
                }
            }
    }

