                val totalSum =
                    summer { faktiskKostnadForMerkjoeringForAaBringeOgHenteBarn.beloepSomTall + reisekostnaderVedSats }
                val harVedlegg = faktiskKostnadForMerkjoeringForAaBringeOgHenteBarn.beloepIValuta har vedlegg

                hvis(ikke(harVedlegg) og ikke(harEndringsaarsakOverfoertEktefelle) og (totalSum erStørreEnn 10_000)) {
