sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val antallOvernattingsdoegnYrkesTjenestereiseOgKjoereoppdrag = skattemelding
            .forForekomsterAv(gruppe(oppholdUnderYrkesOgTjenestereise, oppholdUnderKjoereoppdrag))
            .summer { antallOvernattingsdoegn }

        val antallOvernattingsdøgnPendleropphold = skattemelding
            .forForekomsterAv(sti(pendleropphold, spesifikasjonAvPendlerbolig))
            .summer { antallOvernattingsdoegn }

        skattemelding utslagFor hvis(
            (antallOvernattingsdoegnYrkesTjenestereiseOgKjoereoppdrag + antallOvernattingsdøgnPendleropphold) erStørreEnn 365
        ) {
            betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val pendleroppholdene = skattemelding
            .forForekomsterAv(
                sti(
                    pendleropphold,
                    spesifikasjonAvPendlerbolig
                )
            )

        val overlappendePendleropphold = pendleroppholdene.hvor {
            val start = verdiFor { startdato }.dato
            val slutt = verdiFor { sluttdato }.dato
            val overlapperMedAndreOpphold = pendleroppholdene.filtrer {
                val start2 = verdiFor { startdato }.dato
                val slutt2 = verdiFor { sluttdato }.dato
                (
                    (start2 erStørreEnnEllerLik start) og (start2 erMindreEnn slutt)
                    ) eller (
                    (slutt2 erStørreEnn start) og (slutt2 erMindreEnnEllerLik slutt)
                    )
            }
            overlapperMedAndreOpphold.antall erStørreEnn 1
        }

        // kontrollen gir utslag dersom det er registrert flere pendleropphold på samme periode
        skattemelding
           .fokusområde("Fokusområde")
           .forForekomsterAv(pendleropphold)
           .utslagFor{
              val harPendleropphold = forForekomsterAv(spesifikasjonAvPendlerbolig)
                   .hvorMinstEnForekomst{ fastsatt(startdato) er oppgitt }  

               hvis(harPendleropphold og overlappendePendleropphold) {
                 betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
               }
           }
    }

sakstype(Personlig)
        .kontekst(ValideringSkattepliktig)
    {
    //har godtgjørelse fra arbeidsgiver
            val godtgjoerelseFraarbeidsgiver = skattemelding
                .forForekomsterAv(arbeidsgiver)
                .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                .hvorMinstEnForekomst {
                    behandlingsart.finnesBlant (
                        "BOLIG_UTL_PENDLER", "DIETT_HOTELL",
                        "DIETT_LANGTR_UTL", "DIETT_HYBEL",
                        "DIETT_PENSJONAT", "FRI_KO_LO_BO_PENDLER",
                        "FRI_KOST_LOSJI", "GODTGJ_EOS_PENDLER",
                        "KOSTBESPARELSE", "LOSJI_BRAKKE",
                        "TFRI_KOST_BESOK", "TFRI_KOST_O_28DG",
                        "TFRI_KOST_O_28DG_UT", "TFRI_KOST_TJENESTE",
                        "UTGGJ_REISE_KOST_LOSJI"
                    )
                }

    //kontrollen gir utslag og merknad dersom skattyter har godtgjørelser fra arbeidsgiver
    //og har merkostnad hvor antall døgn er større enn 0
    //og det ikke er oppgitt at merkostnad er dekket av arbeidsgiver
    //merknaden skal kun vises på merkostnadskortet og ikke på lønnskortene
            skattemelding
                .utslagFor {
                //Har ingen merkostnader av type oppholdUnderYrkesOgTjenestereise eller oppholdUnderKjoereoppdrag som er oppgitt med lavere sats
                val merkostnaderUtenGodtgjørelseLavereEnnSats =
                    forForekomsterAv(gruppe(oppholdUnderKjoereoppdrag, oppholdUnderYrkesOgTjenestereise))
                        .hvorMinstEnForekomst {
                            (antallOvernattingsdoegn erStørreEnn 0) og
                                (mottarKostgodtgjoerelseLavereEnnSats erLik false)
                        }

                //har ingen merkostnad av type pendleropphold med godtgjørelse fra arbeidsgiver
                val merkostnaderUtenOppgittDekketAvArbeidsgiver = 
                    forForekomsterAv(sti(pendleropphold,spesifikasjonAvPendlerbolig))
                     .hvorMinstEnForekomst {
                        (antallOvernattingsdoegn erStørreEnn 0) og
                            (utbetaltKostgodtgjoerelseFraArbeidsgiver.beloepSomTall ikkeEr oppgitt)
                    }

                     hvis((merkostnaderUtenOppgittDekketAvArbeidsgiver eller merkostnaderUtenGodtgjørelseLavereEnnSats)  og
                            godtgjoerelseFraarbeidsgiver)

                    {
                        betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                    }
                }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        skattemelding
            .forForekomsterAv(arbeidsgiver)
            .filtrer { (grunnlag(landkode) ikkeEr oppgitt) eller (grunnlag(landkode) erLik "NO") }
            .utslagFor {
                val harEndringsaarsak = forForekomsterAv(aarsakTilEndring)
                    .hvorMinstEnForekomst { aarsak erLik "feilManglendeInnrapportering" }

                val filtrerteForekomster = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .filtrer {
                        (
                            grunnlag(behandlingsart).finnesIkkeBlant(
                                "BILGODT",
                                "GODTGJ_FREMKOMST",
                                "UTGGJ_REISE_KOST_LOSJI",
                                "UTG_GODTGJORELSE"
                            )
                                og grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                                )
                            )
                    }
                val sumGrunnlag = filtrerteForekomster.summer {
                    grunnlag(beloepSomGruppe.beloepSomTall)
                }
                val sumFastsatt = filtrerteForekomster.summer {
                    fastsatt(beloepSomGruppe.beloepSomTall)
                }
                val skalGiUtslag = filtrerteForekomster.hvorMinstEnForekomst {
                    (grunnlag(beloepSomGruppe.beloepSomTall) erStørreEnn 0) og (fastsatt(beloepSomGruppe.beloepSomTall) erMindreEnnEllerLik 0)
                }
                hvis(ikke(harEndringsaarsak) og skalGiUtslag og (sumGrunnlag erUlik sumFastsatt)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { fastsattEllerGrunnlag(partsnummer) }

        val erIkkeSjoeEllerSokkel = skattepliktGjeldende
            .forForekomsterAv(sti(skatteplikt, personligSkattesubjekt, saerskiltSkatteplikt))
            .harIngenForekomsterDer { (type erLik "skattepliktEtterPetroleumsskatteloven") eller (type erLik "skattepliktSomSjoemann") }

        // kontrollen gir utslag dersom skattyter er uten forhold sjø og sokkel og har valgt "ikke skattepliktig" på lønnsinntekt,
        // og skattyters partsnr finnes i kontrollisten - de ligger i kontrollisten hvis SP har oppdrag med "Ja" på FD (fast driftssted), er Innleie i OAR og har NT1 skjema
        // kontrollen gir merknad i SME
        skattemelding
            .utslagFor {

                val harOppgittSkattefriLoenn = forForekomsterAv(arbeidsgiver)
                    .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (
                            fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattefriMedTrygdeavgiftspliktForHelsedel",
                                "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                "skattefriMedTrygdeavgiftsplikt",
                                "skattefriKildeskattPaaPensjon",
                                "skattefriUtenTrygdeavgiftsplikt",
                                "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                            )
                            ) og
                            (
                                ikke(
                                    grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik fastsatt(
                                        beloepSomGruppe.trygdeEllerSkattemessigUnntak
                                    )
                                )
                                )
                    }

                val partsnrIidentifikatorIkontrolliste = finnesIKontrolliste(
                    skattemelding.kontrollKontekst.inntektsaar,
                    "kontrolliste - Ikke skattepliktig loenn med NT1 skjema",
                    listeAv(partsnr)
                )

                hvis(erIkkeSjoeEllerSokkel og harOppgittSkattefriLoenn og partsnrIidentifikatorIkontrolliste) {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
                }
            }
    }

sakstype(Personlig)
