        // Kontrollen gir utslag dersom skattyter har oppgitt bankkonto i utlandet UTEN beløp i noen felter
        // lager en ekstra merknad siden mange som får utvidet veiledning på CRS ikke skjønner at de skal legge inn beløp - de legger bare inn kontoen
        skattemelding
            .forForekomsterAv(konto)
            .filtrer { (landkode er oppgitt) og (landkode erUlik "NO") }
            .utslagFor {
                hvis(
                    (fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(betalteMisligholdteRenter.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(gevinstValutalaan.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(gjeld.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(innskudd.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(opptjenteRenter.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(tapValutalaan.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(tilbakefoertRente.beloepSomTall) ikkeEr oppgitt)
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val erGlobalskattepliktig = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }

        // kontrollen gir utslag dersom skattyter er globalskattepliktig
        // og har valgt "ikke skattepliktig" på lønnsinntekt av type LONN_UTL eller LONN med landkode ulik NO
        // og IKKE har endringsårsak
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(arbeidsgiver)
            .filtrer {
                (landkode er oppgitt) og (landkode erUlik "NO") og
                    (aarsakTilEndring ikkeEr oppgitt)
            }
            .utslagFor {
                val harOppgittSkattefriLoenn = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .filtrer { behandlingsart.finnesBlant("LONN", "LONN_UTL") }
                    .hvorMinstEnForekomst {
                        fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                            "skattefriMedTrygdeavgiftsplikt",
                            "skattefriKildeskattPaaPensjon",
                            "skattefriUtenTrygdeavgiftsplikt",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                        )
                    }

                hvis(erGlobalskattepliktig og harOppgittSkattefriLoenn) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har nullstilt utenlandske lønn (med landkode) av type LONN eller LONN_UTL med grunnlag(et utslag pr arbeidsgiver)
        // gir ikke utslag dersom lønnen er merket med skattefritt unntak, da det gir utslag på annen kontroll
        skattemelding
            .forForekomsterAv(arbeidsgiver)
            .filtrer { (grunnlag(landkode) er oppgitt) og ikke(grunnlag(landkode) erLik "NO") }
            .utslagFor {
                val forekomsterSomGirUtslag = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (grunnlag(behandlingsart).finnesBlant("LONN", "LONN_UTL")) og
                            (
                                grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada"
                                )
                                ) og
                            (grunnlag(beloepSomGruppe.beloepSomTall) erStørreEnn 0) og
                            ikke(fastsatt(beloepSomGruppe.beloepSomTall) erStørreEnn 0)
                    }

                hvis(forekomsterSomGirUtslag) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
        .kontekst(ValideringSkattepliktig)
    {
    //kontrollen gir utslag og merknad dersom skattyter har skatteplikt = global
    //dersom skattyter legger inn kr 0 på preutfylt LONN eller LONN_UTL med landkode oppgitt og ulik NO
    val harGlobalSkatteplikt = skattepliktGjeldende
        .forForekomsterAv(skatteplikt)
        .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "global" }

           skattemelding
                .forForekomsterAv(arbeidsgiver)
                .filtrer { (grunnlag(landkode) er oppgitt) og ikke(grunnlag(landkode) erLik "NO") }
                .utslagFor {
                    val forekomsterSomGirUtslag = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .hvorMinstEnForekomst {
                            (grunnlag(behandlingsart).finnesBlant("LONN", "LONN_UTL")) og
                                (grunnlag(beloepSomGruppe.beloepSomTall) erStørreEnn 0) og
                                ikke(fastsatt(beloepSomGruppe.beloepSomTall) erStørreEnn 0)
                         }
            hvis(harGlobalSkatteplikt og forekomsterSomGirUtslag) {
                betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
            }
        }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har lagt til valutalåndstap på konto
        skattemelding
            .forForekomsterAv(konto)
            .filtrer { grunnlag(tapValutalaan.beloepSomTall) erLik 0 }
            .utslagFor {
                val differanseTap =
                    (fastsatt(tapValutalaan.beloepSomTall) - grunnlag(tapValutalaan.beloepSomTall))
                val harVedlegg = tapValutalaan.beloepIValuta har vedlegg

                // skal kun gi merknad i SME fra 50 000 til 5 mill (for å unngå flere merknader på samme beløp)
                hvis(ikke(harVedlegg) og (differanseTap erStørreEnnEllerLik 50_000) og (differanseTap erMindreEnn 5_000_000)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadDokumentasjon,
                        1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { fastsattEllerGrunnlag(partsnummer) }

        // AB-test: kontrollen gir utslag dersom skattyter har redusert skattepliktig gevinst på konto
        // kontrollen gir utslag på skattytere med 0,2,4,6 eller 8 i 7.siffer i partsnummeret
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(konto)
            .filtrer { grunnlag(gevinstValutalaan.beloepSomTall) erStørreEnn 0 }
            .utslagFor {
                val differanseGevinst =
                    (grunnlag(gevinstValutalaan.beloepSomTall) - fastsatt(gevinstValutalaan.beloepSomTall))
                val harVedlegg = gevinstValutalaan.beloepIValuta har vedlegg

                // skal kun gi merknad i SME fra 50 000 til 5 mill (for å unngå flere merknader på samme beløp)
                hvis(
                    ikke(harVedlegg) og (partsnr.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8")) og
                        ((differanseGevinst erStørreEnnEllerLik 50_000) og (differanseGevinst erMindreEnn 5_000_000))
                ) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadDokumentasjon,
                        1
                    )
                }
            }
    }

