    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har endret utbytte på verdipapirfond med 5 mill eller mer
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(verdipapirfond)
            .utslagFor {
                val differanseUtbytte = differanse(
                    grunnlag(skattepliktigUtbytte.beloepSomTall),
                    fastsatt(skattepliktigUtbytte.beloepSomTall)
                )

                hvis(differanseUtbytte erStørreEnnEllerLik 5_000_000) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(KontrollSaksbehandler, ValideringSkattepliktig) {
        // kontrollen gir utslag dersom de har rett til skattebegrensning §17.4, og har fastsatt at de ønskes å vurderes for liten skatteevne
        skattemelding
            .forForekomsterAv(skattebegrensningParagraf174)
            .utslagFor {
                hvis(
                    (fastsatt(vurderesForSkattebegrensningParagraf174LitenSkatteevne) erLik true) og
                        (grunnlag(rettTilSkattebegrensningParagraf174LitenSkatteevneForegaaendeAar) erLik true)
                ) {
                    betjeningsstrategiSettesTil(
                        myndighetsfastsettingMedVarselOgVedtak,
                        merknadDokumentasjon,
                        1,
                        Gruppering settesTil AndreForhold,
                        spesialomraade,
                        rettighetskontroll
                    )
                }
            }
    }
