kontrollformel:
sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        // kontrollen gir utslag dersom skattyter ikke har kort med lønn fra utlandet
        // og ligger i kontrolliste
        skattemelding
            .utslagFor {

                val harIngenForekomsterAvUtenlandskLoenn = forForekomsterAv(arbeidsgiver)
                    .harIngenForekomsterDer { (fastsatt(landkode) er oppgitt) og (fastsatt(landkode) erUlik "NO") }

                val utenlandskLoennIkontrolliste = finnesIKontrolliste(
                    skattemelding.kontrollKontekst.inntektsaar,
                    "kontrolliste - gld med loenn i Utlandet",
                    listeAv(partsnr)
                )

                hvis(harIngenForekomsterAvUtenlandskLoenn og utenlandskLoennIkontrolliste) {
                    betjeningsstrategiSettesTil(ikke, merknadTips, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig)
{
skattemelding utslagFor hvis(
    (arbeidsgiver hvorMinstEnForekomst (samledeYtelserFraArbeidsgiverPerBehandlingsart
        hvorMinstEnForekomst (beloepSomGruppe.metodeVedDobbeltbeskatning erLik "denAlternativeFordelingsmetoden")))
        eller (pensjonsinntekt hvorMinstEnForekomst (perMaaned hvorMinstEnForekomst (
        pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning erLik "denAlternativeFordelingsmetoden")))
) {
    hvis(ikke(tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden er oppgitt)) {
        betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
    }
}
}

