sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har sykepenger eller dagpenger, og har arbeidsreiser med tilsammen 230 dager eller mer - gir merknad på arbeidsreisene
        val harBehandlingsartSykepengerDagpenger = skattemelding
            .forForekomsterAv(arbeidsgiver)
            .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
            .hvorMinstEnForekomst {
                (behandlingsart erLik "DAGPENGER") eller (behandlingsart erLik "SYKEPENGER")
            }

        skattemelding
            .forForekomsterAv(reiserIArbeidsforhold)
            .utslagFor {
                val sumAntallArbeidsreisedager = forForekomsterAv(arbeidsreise)
                    .summer { antallReiser }

                hvis((sumAntallArbeidsreisedager erStørreEnnEllerLik 230) og (harBehandlingsartSykepengerDagpenger)) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harHoeyeFradragFormerkostnader = skattemelding
            .forForekomsterAv(arbeidsoppholdUtenforHjemmet)
            .hvorMinstEnForekomst {
                fradragForMerkostnadVedArbeidsoppholdUtenforHjemmetSomGruppe.fradragsberettigetBeloep.beloepSomTall erStørreEnn 10_000
            }

        // kontrollen gir utslag dersom skattyter har fradrag for merkostnader ved kost og losji på mer enn 10 000
        // kontrollen gir merknad i SME
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(
                gruppe(
                    sti(pendleropphold, spesifikasjonAvPendlerbolig),
                    oppholdUnderYrkesOgTjenestereise,
                    oppholdUnderKjoereoppdrag
                )
            )
            .utslagForFørste {
                hvis(
                    (antallOvernattingsdoegn erStørreEnn 0) og
                        harHoeyeFradragFormerkostnader
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
                }
            }
    }

sakstype(Personlig)
