    .forForekomsterAv(gruppe(sti(pendleropphold,spesifikasjonAvPendlerbolig), oppholdUnderYrkesOgTjenestereise, oppholdUnderKjoereoppdrag))
    .utslagFor {
        hvis((antallOvernattingsdoegn erStørreEnn 0)  og
            harHoeyeFradragFormerkostnader)
        {
            betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
        }
    }
}

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt formue på aksjer i AR er ulik grunnlag med 100 mill eller mer - kun merknad i SME
        skattemelding
            .forForekomsterAv(aksjeIAksjonaerregisteret)
            .utslagFor {
                val differanseFormue = differanse(
                    fastsatt(verdiFoerVerdsettingsrabattForAksje.beloepSomTall),
                    grunnlag(verdiFoerVerdsettingsrabattForAksje.beloepSomTall)
                )

                hvis(ikke(harInnsending) og (differanseFormue erStørreEnnEllerLik 100_000_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1,
                        Gruppering settesTil Finans,
                        spesialomraade,
                        Avviksbeloep settesTil differanseFormue
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(KontrollSaksbehandler, ValideringSkattepliktig) {
        // gir utslag dersom oppgitt gevinst på aksjer i AR er ulik grunnlag med 20 mill eller mer
        skattemelding
            .forForekomsterAv(aksjeIAksjonaerregisteret)
            .utslagFor {
                val differanseGevinst = differanse(
                    fastsatt(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall),
                    grunnlag(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall)
                )

                hvis(differanseGevinst erStørreEnnEllerLik 20_000_000) {
                    betjeningsstrategiSettesTil(
                        myndighetsfastsettingMedVarselOgVedtak, merknadStandard, 1,
                        Gruppering settesTil Finans,
                        spesialomraade,
                        Avviksbeloep settesTil differanseGevinst
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(KontrollSaksbehandler, ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt/fastsatt gevinst på aksje i AR er midre enn grunnlag
        skattemelding
            .forForekomsterAv(aksjeIAksjonaerregisteret)
            .filtrer { grunnlag(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) er oppgitt }
            .utslagFor {
                val differanseGevinst =
                    (
                        grunnlag(skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall) - fastsatt(
                            skattepliktigGevinstVedRealisasjonAvAksje.beloepSomTall
                        )
                        )

                // Skal kun gi merknad i SME og oppgave i SMIA fra 50 000 til 20 mill (for å unngå flere merknader/oppgaver på samme beløp)
                hvis((differanseGevinst erStørreEnnEllerLik 50_000) og (differanseGevinst erMindreEnn 20_000_000)) {
                    betjeningsstrategiSettesTil(
                        myndighetsfastsettingMedVarselOgVedtak, merknadStandard, 1,
                        Gruppering settesTil Finans,
                        spesialomraade, ikkeObligatorisk,
                        Avviksbeloep settesTil differanseGevinst
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(KontrollSaksbehandler, ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt/fastsatt utbytte på aksje i AR er midre enn grunnlag
        skattemelding
            .forForekomsterAv(aksjeIAksjonaerregisteret)
            .filtrer { grunnlag(skattepliktigUtbytte.beloepSomTall) er oppgitt }
            .utslagFor {
                val differanseUtbytte =
                    (grunnlag(skattepliktigUtbytte.beloepSomTall) - fastsatt(skattepliktigUtbytte.beloepSomTall))

                // Skal kun gi merknad i SME og oppgave i SMIA fra 50 000 til 20 mill (for å unngå flere merknader/oppgaver på samme beløp)
                hvis((differanseUtbytte erStørreEnnEllerLik 50_000) og (differanseUtbytte erMindreEnn 20_000_000)) {
