sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        //kontrollen gir utslag dersom skattyter har lønnsinntekt og ikke har oppgitt trygdefritak på noe lønn,
        //og skattyters partsnr finnes i kontrollisten - de ligger i kontrollisten hvis vi har mottatt opplysninger fra NAV om at SP har trygdefritak kun for deler av inntektsåret
        //kontrollen gir merknad i SME
        skattemelding
            .utslagFor {

                val harIkkeOppgittTrygdefriLoenn = forForekomsterAv(arbeidsgiver)
                    .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .filtrer{fastsatt(beloepSomGruppe.beloepSomTall) erStørreEnn 0}
                    .harIngenForekomsterDer {
                        fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
                            "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel",
                            "skattefriUtenTrygdeavgiftsplikt",
                            "skattepliktigUtenTrygdeavgiftsplikt"
                        )
                    }          

                val partsnrIidentifikatorIkontrolliste = finnesIKontrolliste(
                    skattemelding.kontrollKontekst.inntektsaar,
                    "kontrolliste - NAV trygdefritak deler av aaret",
                    listeAv(partsnr)
                )

                hvis(harIkkeOppgittTrygdefriLoenn og partsnrIidentifikatorIkontrolliste)
                {
                    betjeningsstrategiSettesTil(ikke, merknadInformasjon, 1)
                }
            }
    }

