sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        // Kontrollen gir utslag dersom skattyter har oppgitt gjeldsrenter som er høyere enn 10% av gjelden (skal ikke gi utslag dersom alt er preutfylt)
        // AB-test på skattytere som har tallet 2, 3, 4, 5, 6, 7, 8, 9 i 7.siffer i partsnummeret
        // Gir merknad
        skattemelding
            .forForekomsterAv(konto)
            .filtrer { fastsatt(gjeldSomGruppe.beloepSomTall) erStørreEnn 50_000 }
            .utslagFor {

                hvis(
                    (
                        fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloepSomTall) erStørreEnn grunnlag(
                            paaloepteRenter.fradragsberettigetBeloep.beloepSomTall
                        )
                        ) og
                        (
                            fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloepSomTall) erStørreEnn (
                                fastsatt(
                                    gjeldSomGruppe.beloepSomTall
                                ) * 0.1
                                )
                            ) og
                        (fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloepSomTall) erMindreEnnEllerLik 1_000_000) og
                        (partsnr.tegnFraIndeks(7).finnesBlant("2", "3", "4", "5", "6", "7", "8", "9"))
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
.kontekst(ValideringSkattepliktig) {
    val boligForekomster = skattemelding
        .forForekomsterAv(gruppe(formuesgrunnlagSelveidBolig, formuesgrunnlagBoenhetIBoligselskap))
        .filtrer { fastsatt(internEiendomsidentifikator) er oppgitt }

    val eiendommerMedEndretEierandel = skattemelding
        .forForekomsterAv(fastEiendom)
        .filtrer {
            (grunnlag(eierandel) er oppgitt) og
                (fastsatt(eierandel) erMindreEnnEllerLik (grunnlag(eierandel) * 0.9))
        }
        .verdierFor { internEiendomsidentifikator }

    val boligForekomsterMedEndretEierandel = boligForekomster
        .hvor { internEiendomsidentifikator finnesBlant eiendommerMedEndretEierandel }

    // Kontrollen gir utslag på alle eiendommer som skattyter har satt ned eierandel med minimum 10%
    //gir bare utslag dersom det mangler vedlegg/dokumentasjon
    skattemelding
        .utslagFor {
            val harIkkeVedlegg =
                forForekomsterAv(fastEiendom) hvorMinstEnForekomst { eierandel harIkke vedlegg }

            hvis(ikke(harInnsending) og harIkkeVedlegg og (boligForekomsterMedEndretEierandel)) {
                betjeningsstrategiSettesTil(
                    ikke, merknadDokumentasjon, 1)
            }
        }
}

