sakstype(Personlig)
.kontekst(ValideringSkattepliktig) {
    // Sjekker om skatteyter har utvidet veiledningstype "UTLEIE_AV_FAST_EIENDOM" i utvidetVeiledningDokument
    val harUtvidetVeiledningPaaUtleie = utvidetVeiledning
        .forForekomsterAv { utvidetveiledning }
        .forForekomsterAv { metadata }
        .hvorMinstEnForekomst { type erLik "UTLEIE_AV_FAST_EIENDOM" }

    // Returnerer partsnummeret som et tekstfelt
    val partsnr = skattepliktGjeldende
        .forForekomsterAv(skatteplikt)
        .verdiFor { fastsattEllerGrunnlag(partsnummerSomTekst) }

    // AB-test: kontrollen gir utslag dersom skattyter hadde utleie i fjor, men ikke har oppgitt noen utleie i år,
    // og heller ikke har fått utvidet veilelding, på skattytere som har tallet 4 eller 5 i 7.siffer i partsnummeret
    // kontrollen gir merknad i SME og skal også ha annen merknad om utleie sekundærbolig
    val harUtleidFjoraaret = fjoraaretsSkattemelding
        .forForekomsterAv(
            gruppe(
                inntektsaar2020.sjablongbehandletUtleie,
                inntektsaar2020.regnskapsbehandletUtleie,
                inntektsaar2020.sjablongOgRegnskapsbehandletUtleie
            )
        ).hvorMinstEnForekomst {
            ((fastsatt(inntektsaar2020.bruttoLeieinntektVedSjablongbehandletUtleie.beloepSomTall) erStørreEnn 0)
                eller (fastsatt(inntektsaar2020.bruttoLeieinntektVedRegnskapsbehandletUtleie.beloepSomTall) erStørreEnn 0))
        }

    skattemelding utslagFor hvis(
        gruppe(
            sjablongbehandletUtleie,
            regnskapsbehandletUtleie,
            sjablongOgRegnskapsbehandletUtleie
        ) ikkeEr oppgitt
            og (partsnr.tegnFraIndeks(7).finnesBlant("4", "5"))
            og harUtleidFjoraaret
            og ikke(harUtvidetVeiledningPaaUtleie)
    ) {
        betjeningsstrategiSettesTil(ikke, merknadTips, 1)
    }
}

