sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val boligForekomsterSomSkattyterHarEndret = skattemelding
            .forForekomsterAv(gruppe(formuesgrunnlagSelveidBolig, formuesgrunnlagBoenhetIBoligselskap))
            .filtrer {
                (fastsatt(internEiendomsidentifikator) er oppgitt) og
                    (
                        (fastsatt(byggeaar) erUlik grunnlag(byggeaar)) eller
                            (fastsatt(boligensAreal) erUlik grunnlag(boligensAreal)) eller
                            (fastsatt(boligtype) erUlik grunnlag(boligtype)) eller
                            (fastsatt(dokumentertMarkedsverdi.beloepSomTall) erUlik grunnlag(dokumentertMarkedsverdi.beloepSomTall))
                        )
            }

        val eiendommerMedEierandelUnder100Prosent = skattemelding
            .forForekomsterAv(fastEiendom)
            .filtrer { (fastsatt(eierandel) erStørreEnn 0) og (fastsatt(eierandel) erMindreEnn 100) }
            .verdierFor { internEiendomsidentifikator }

        val endredeBoligForekomsterMedEierandelUnder100Prosent = boligForekomsterSomSkattyterHarEndret
            .hvor { internEiendomsidentifikator finnesBlant eiendommerMedEierandelUnder100Prosent }

        // Kontrollen gir utslag og merknad på alle eiendommer hvor skattyter har endret byggeår, areal, boligtype eller markedsverdi
        // gir bare utslag dersom skattyter eier mindre enn 100 %
        skattemelding
            .utslagFor(
                hvis(ikke(harInnsending) og endredeBoligForekomsterMedEierandelUnder100Prosent) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1
                    )
                }
            )
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        val erIkkeSjoeEllerSokkel = skattepliktGjeldende
            .forForekomsterAv(sti(skatteplikt, personligSkattesubjekt, saerskiltSkatteplikt))
            .harIngenForekomsterDer { (type erLik "skattepliktEtterPetroleumsskatteloven") eller (type erLik "skattepliktSomSjoemann") }

        // kontrollen gir utslag dersom skattyter har lønn
        // dersom skattyter er uten forhold sjø og sokkel,
        // og skattyters partsnr finnes i kontrollisten - de ligger i kontrollisten hvis SP har oppdrag av type Entreprise og ikke har "Ja" på FD (fast driftssted) i OAR
        // kontrollen gir merknad i SME
        skattemelding
            .utslagFor {

                val partsnrIidentifikatorIkontrolliste = finnesIKontrolliste(
                    skattemelding.kontrollKontekst.inntektsaar,
                    "kontrolliste - Entreprise uten fast driftsted",
                    listeAv(partsnr)
                )

                val harLoenn = forForekomsterAv(arbeidsgiver)
                    .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst { fastsatt(beloepSomGruppe.beloepSomTall) erStørreEnn 0 }

                hvis(harLoenn og erIkkeSjoeEllerSokkel og partsnrIidentifikatorIkontrolliste) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

