            }
            .utslagFor {
                val differanseFormueVerdi =
                    (
                        grunnlag(verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloepSomTall) - fastsatt(
                            verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloepSomTall
