sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harOppgittTilleggsinfoOmKPP = skattemelding
            .forForekomsterAv(tilleggsinformasjonVedKildeskattPaaPensjon)
            .hvorMinstEnForekomst { bosattILandEtterSkatteavtale er oppgitt }

        // Kontrollen gir utslag hvis særskilt skatteplikt på pensjon = kildeskattepliktPaaPensjon
        // og skattyter selv har valgt skatte- og trygdemessig unntak lik skattefriKildeskattPaaPensjon på minst en måned av pensjonen
        // og skattyter ikke har oppgitt tilleggsinfo  ved kildeskatt på pensjon
        skattemelding
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { fastsatt(saerskiltSkattepliktSomTekst) erLik "kildeskattepliktPaaPensjon" }
            .utslagFor {

                val egenfastsattIkkeSkattepliktigKPP = forForekomsterAv(perMaaned)
                    .hvorMinstEnForekomst {
                        (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriKildeskattPaaPensjon") og
                            (
                                fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erUlik grunnlag(
                                    pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak
                                )
                                )
                    }

                hvis(ikke(harInnsending) og egenfastsattIkkeSkattepliktigKPP og ikke(harOppgittTilleggsinfoOmKPP)) {
