sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val erKPPOgHarSatsEtterAvtale = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .forForekomsterAv(sti(personligSkattesubjekt, saerskiltSkatteplikt))
            .hvorMinstEnForekomst { (type erLik "kildeskattepliktPaaPensjon") og (satsEtterSkatteavtale er oppgitt) }

        // Gir utslag på skattytere som er KPP i skatteplikt
        // og Sats etter skatteavtale er oppgitt i skatteplikt (0-14)
        // Skal bare gi utslag dersom skattyter ikke har krysset av for "Jeg krever å bli skatteberegnet som bosatt i Norge" 
        // Denne avkrysningen lagres ikke i skattemeldingen, må derfor sjekke på om endringsårsak = kildeskattPensjonKreverBeskattetSomBosatt (denne settes automatisk ved avkryssning)

        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { fastsatt(saerskiltSkattepliktSomTekst) erLik "kildeskattepliktPaaPensjon" }
            .utslagFor {

                val harPensjon = fastsatt(pensjonsinntektPrSkattleggingsperiode.beloepSomTall) erStørreEnn 0

                val harIkkeKrevdAaBliSkatteberegnetSomBosattINorge = forForekomsterAv(aarsakTilEndring)
                     .harIngenForekomsterDer{fastsatt(aarsak) erLik "kildeskattPensjonKreverBeskattetSomBosatt"}




                hvis(erKPPOgHarSatsEtterAvtale og harPensjon og harIkkeKrevdAaBliSkatteberegnetSomBosattINorge) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val harKrevdAaBliSkatteberegnetSomBosattINorge = skattemelding
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { (fastsatt(saerskiltSkattepliktSomTekst) erLik "kildeskattepliktPaaPensjon") og 
                       (fastsatt(pensjonsinntektPrSkattleggingsperiode.beloepSomTall) erStørreEnn 0) }
            .forForekomsterAv(aarsakTilEndring)
            .hvorMinstEnForekomst{fastsatt(aarsak) erLik "kildeskattPensjonKreverBeskattetSomBosatt"}

        // Henter landkoder i EØS for 2021 fra kodeliste
        val EOESLandkoder = aktiveLandkoder_2021
            .forForekomsterAv { kode }
            .filtrer { kodetillegg.medlemAvEoes erLik "Ja" }
            .verdierFor { tekniskNavn }

        // kontrollen gir utslag dersom skattyter er KPP og har krysset av for "Jeg krever å bli skatteberegnet som bosatt i Norge"
        // (dette flagget blir ikke lagret i skattemeldingen, så må derfor sjekke på endringsårsak kildeskattPensjonKreverBeskattetSomBosatt som settes automatisk)
        // og har lagt inn EØS land under tilleggsinfo for KPL
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(tilleggsinformasjonVedKildeskattPaaPensjon)
            .utslagFor {
                val harVedlegg = bosattILandEtterSkatteavtale har vedlegg
                hvis(
                    harKrevdAaBliSkatteberegnetSomBosattINorge og (bosattILandEtterSkatteavtale.finnesBlant(EOESLandkoder)) og
