sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val landMedKredit = metodeVedDobbeltbeskatningPrLand_2021
            .forForekomsterAv { kode }
            .filtrer {kodetillegg.hovedmetode erLik "Kredit"}
            .verdierFor { tekniskNavn }

        val landHvorDetErBetaltSkattIUtlandetOppgittIFjoraaret = fjoraaretsSkattemelding
            .forForekomsterAv(kravOmKreditfradragForUtenlandskSkatt)
            .filtrer {
                (fastsatt(inntektsaar2020.kravOmKreditfradragForUtenlandskFormuesskatt.beloepSomTall) erStørreEnn 0) eller
                (fastsatt(inntektsaar2020.kravOmKreditfradragForUtenlandskInntektsskatt.beloepSomTall) erStørreEnn 0) eller
                (fastsatt(inntektsaar2020.skattBetaltPaaAksjeutbytteMottattFraUtenlandskSelskap.beloepSomTall) erStørreEnn 0) eller
                (fastsatt(inntektsaar2020.bruttoAksjeutbytteMottattFraUtenlandskSelskap.beloepSomTall) erStørreEnn 0)
            }
            .verdierFor{fastsatt(landkode)}

        // Kontrollen gir utslag dersom skattyter krevde fradrag for skatt betalt i utlandet (kreditfradrag) i fjor,
        // men ikke har oppgitt betalt skatt i utlandet i år, og har et kort med landkode som har kreditmetoden 
        //og det er samme land som det er oppgitt krav om kreditfradrag/ betalt skatt skatt på i fjoråret
        skattemelding
            .utslagFor {
                val forekomsterMedLandSomHarKreditmetodenOgKrevdKreditIFjor = forAlleForekomster
                    .hvorMinstEnForekomst { landkode.finnesBlant(landMedKredit) og (landkode.finnesBlant(landHvorDetErBetaltSkattIUtlandetOppgittIFjoraaret)) }

                hvis(
                    forekomsterMedLandSomHarKreditmetodenOgKrevdKreditIFjor og ikke(
                        kravOmKreditfradragForUtenlandskSkatt er oppgitt
                    )
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadTips, 1)
                }
            }
    }

