            .forForekomsterAv(sti(fordeltBeregnetPersoninntekt, spesifikasjonAvSkjermingsgrunnlag))
            .utslagFor {
                val inngåendeVerdiForetaksgjeld = filtrer { fastsatt(skjermingsgrunnlagstype) erLik "foretaksgjeld" }
