                val gjennomsnittsverdiForetaksgjeld =
                    (inngåendeVerdiForetaksgjeld + utgåendeVerdiForetaksgjeld) / 2

                hvis(gjennomsnittsverdiForetaksgjeld erMindreEnn 0) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        faktiskFeil,
                        1,
                        Avviksbeloep settesTil gjennomsnittsverdiForetaksgjeld
