        // kontrollen gir utslag dersom skattytere hvor forretningsadresse er lik bostedsadresse i folkeregisteret og beløp i feltet 'Leie lokale' er mellom kr 1800 - 150 000.
        // AB-test på skattytere som har tallet 2, 3, 4, 5, 6, 7, 8, 9 i 7.siffer i partsnummeret
        // kontrollen gir merknad

        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { fastsattEllerGrunnlag(partsnummerSomTekst) }

        naeringsspesifikasjon
            .forForekomsterAv(resultatregnskap.driftskostnad.annenDriftskostnadSomGruppe)
            .utslagFor {

                val harLeiekostnad = forForekomsterAv(kostnad)
                    .filtrer { fastsatt(type) erLik "6300" }
                    .summer { beloepSomGruppe.beloepSomTall }

                val finnesIKontrollisteHjemmekontor = finnesIKontrolliste(
                    skattemelding.kontrollKontekst.inntektsaar,
                    "kontrolliste - fradrag hjemmekontor",
                    listeAv(partsnr)
                )

                hvis(
