sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        //kontrollen gir utslag dersom innrapportert næringsinntekt er større enn sum driftsinntekt i resultatregnskapet
    	//kontrollen gir merknad

        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { fastsattEllerGrunnlag(partsnummerSomTekst) }

        val sumInnrapportertNæringsinntekt = skattemelding
      		.forForekomsterAv(oppgavegiver)
        	.summer{inntektFraUtbetalingTilSelvstendigNaeringsdrivende.beloepSomTall}

    naeringsspesifikasjon        
            .utslagFor { 

                val driftsinntekterSummert = forForekomsterAv(sti(resultatregnskap, driftsinntekt, salgsinntekt))
              		.forForekomsterAv(inntekt)
            		.filtrer{fastsatt(type).finnesBlant("3000","3100","3200","3300")}
              		.summer{beloepSomGruppe.beloepSomTall}

                val finnesIKontrollisteTredjepart = finnesIKontrolliste(
                    skattemelding.kontrollKontekst.inntektsaar,
                    "kontrolliste - innrapportert naeringsinntekt hoyere enn driftsinntekt",
                    listeAv(partsnr)            )

                val verdiFraKontrollist = hentKontrolliste(
        				skattemelding.kontrollKontekst.inntektsaar,
        				"kontrolliste - innrapportert naeringsinntekt hoyere enn driftsinntekt",
        				listeAv(partsnr)
        				)
        				.forForekomsterAv {verdi}
           				.summer{verdiSomTall1}

                val avvikBeløp = verdiFraKontrollist - sumInnrapportertNæringsinntekt - driftsinntekterSummert

                hvis(finnesIKontrollisteTredjepart og (avvikBeløp erStørreEnnEllerLik 1000) )            
                 {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1, Avviksbeloep settesTil avvikBeløp,
                    	kortTypeReferanse settesLik "salgsinntekt")
                }
            }}

