sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når beløpet fylt ut på feltet 'Kompensasjon for tapt inntekt' avviker fra innrapportert beløp fra NAV. Gjelder både når beløpet er større eller mindre enn innrapportert beløp.
        // kontrollen gir merknad i valideringstjenesten

        skattemelding
            .forForekomsterAv(sti(oppgavegiver, sykepengerOgAnnenInntektMvFraOppgavegiver))
            .utslagFor {

                val harKompensasjonNAV = filtrer { grunnlag(behandlingsart) erLik "KOMP_PERS_INNT" }
                    .hvorMinstEnForekomst { grunnlag(beloepSomGruppe.beloepSomTall) er oppgitt }

                val fastsattKompensasjonNAV = filtrer { fastsatt(behandlingsart) erLik "KOMP_PERS_INNT" }
                    .summer { fastsatt(beloepSomGruppe.beloepSomTall) }

                val grunnlagKompensasjonNAV = filtrer { grunnlag(behandlingsart) erLik "KOMP_PERS_INNT" }
                    .summer { grunnlag(beloepSomGruppe.beloepSomTall) }

                val differanseKompensasjonNAV = fastsattKompensasjonNAV - grunnlagKompensasjonNAV

                hvis(harKompensasjonNAV og (differanseKompensasjonNAV erUlik 0)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1,
                        Avviksbeloep settesTil differanseKompensasjonNAV
                    )
                }
            }
    }

