    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyters partsnr finnes i kontrollisten (er næringsdrivende) og
        // ikke har oppgitt næringsspesifikasjon eller opplysninger om deltakers andel i SDF
        // kontrollen gir merknadMangel i SME
        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        val finnesIKontrollisteErNaeringsdrivende = finnesIKontrolliste(
            "kontrolliste - naeringsdrivende",
            listeAv(partsnr)
        )

        val harOppgittNaeringsspesifikasjon = naeringsspesifikasjon
            .forForekomsterAv(virksomhet)
            .hvorMinstEnForekomst { virksomhetstype er oppgitt }

        val harIkkeOppgittDeltakerinformasjonSDF = skattemelding
            .harIngenForekomsterDer { fastsatt(deltakersAndelAvFormueOgInntekt) er oppgitt }

        skattemelding
            .utslagFor {
                hvis(
                    finnesIKontrollisteErNaeringsdrivende og
                        ikke(harOppgittNaeringsspesifikasjon) og
                        harIkkeOppgittDeltakerinformasjonSDF
                ) {
                    betjeningsstrategiSettesTil(ikke, faktiskFeil, 1)
                }
