            .forForekomsterAv(fordeltBeregnetNaeringsinntekt)
            .utslagFor {
                val næringstypeSkogbruk = hvorMinstEnForekomst { naeringstype erLik "skogbruk" }

                val differanseSjablongberegnetFradrag =
                    sjablongberegnetInntektFraBiomasseVedproduksjon.beloepSomTall + sumSjablongberegnetFradragISkogbruk
                // må bruke pluss i beregningen siden sjablongberegnetInntektFraBiomasseVedproduksjon.beloepSomTall angis med minus i fordeltBeregnetNaeringsinntekt

                hvis(næringstypeSkogbruk og (sjablongberegnetInntektFraBiomasseVedproduksjon.beloepSomTall erUlik (sumSjablongberegnetFradragISkogbruk * -1))) {
