sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val erKPPISkatteplikt = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .forForekomsterAv(sti(personligSkattesubjekt, saerskiltSkatteplikt))
            .hvorMinstEnForekomst {
                type erLik "kildeskattepliktPaaPensjon"
            }

        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        val harSkattepliktTilSvalbard = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst {
                personligSkattesubjekt.skattepliktTilSvalbard.finnesBlant(
                    "global",
                    "begrenset",
                    "skattepliktUtenOpphold"
                )
            }

        val harDnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst {
                (personligSkattesubjekt.norskPersonidentifikatorSomGruppe.dnummer er oppgitt) og
                    personligSkattesubjekt.norskPersonidentifikatorSomGruppe.dnummer.tegnFraIndeks(1)
                        .finnesBlant("4", "5", "6", "7", "8", "9")
            }

        val sumEtterbetaltLoenn = skattemelding
            .forForekomsterAv(arbeidsgiver)
            .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
            .filtrer { grunnlag(erEtterbetaling) erLik "true" }
            .summerIkkeInkluderForekomster { grunnlag(beloepSomGruppe.beloepSomTall) }

        val sumEtterbetaltPensjon = skattemelding
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { grunnlag(erEtterbetaling) erLik "true" }
            .summerIkkeInkluderForekomster { grunnlag(pensjonsinntektPrSkattleggingsperiode.beloepSomTall) }

        val sumEtterbetaling = (sumEtterbetaltLoenn + sumEtterbetaltPensjon)

        // kontrollen gir utslag dersom skattyter har etterbetaling på 50 000 eller mer
        // Skal IKKE gi utslag på de med skatteplikt til svlabard, kpp eller begrenset skatteplikt
        // de med dnr (fnr som starter med høyere tall enn 3) ikke skal få utslag
        skattemelding
            .utslagFor {
                hvis(
                    ikke(harInnsending) og harIngenForekomsterDer { omberegningsresultatForEtterbetaltePensjonerOgTrygdeytelser er oppgitt } og
                        (sumEtterbetaling erStørreEnnEllerLik 50_000) og
                        ikke(erKPPISkatteplikt) og
                        ikke(harSkattepliktTilSvalbard) og
                        ikke(harBegrensetSkatteplikt) og
                        ikke(harDnr)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon, 1
                    )
                }
            }
    }

