sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harUtslagPaaKontroll = utslagPåKontroll("KPP_REDUKSJON_AV_PREUTFYLT_BELØP")

        // kontrollen gir utslag og merknad dersom skattyter reduserer preutfylt pensjon
        // og skattyter ikke har utslag på kontrollen KPP_REDUKSJON_AV_PREUTFYLT_BELØP
        skattemelding
            .forForekomsterAv(pensjonsinntekt)
            .filtrer { grunnlag(utbetalersNavn) er oppgitt }
            .utslagFor {
                val sumPensjonPerUtbetaler = forForekomsterAv(perMaaned)
                    .summer { pensjonsinntektSomGruppe.beloepSomTall }

                val pensjonGrunnlagMinusFastsatt =
                    grunnlag(sumPensjonPerUtbetaler) - fastsatt(sumPensjonPerUtbetaler)

                hvis(
                    ikke(harInnsending) og (pensjonGrunnlagMinusFastsatt erStørreEnn 0) og ikke(
                        harUtslagPaaKontroll
                    )
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

