sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har ner enn en bolig som er primærbolig (ser bort fra evt  boliger som er overført barn)
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(
                gruppe(
                    formuesgrunnlagSelveidBolig,
                    formuesgrunnlagBoenhetIBoligselskap,
                    formuesgrunnlagFlerboligbygning
                )
            )
            .filtrer { fastsatt(boligbruk) erLik "primaerbolig"}
            .utslagForFørste {

                val harEiendom = hvorMinstEnForekomst {internEiendomsidentifikator er oppgitt}

                val primaerbolig = skattemelding
                    .forForekomsterAv(gruppe(formuesgrunnlagSelveidBolig, formuesgrunnlagBoenhetIBoligselskap))
                    .filtrer { (fastsatt(boligbruk) erLik "primaerbolig") og (fastsatt(overfoertFraBarn.norskPersonidentifikator) ikkeEr oppgitt) }

                val primaerboligIFlerboligbygg = skattemelding
                    .forForekomsterAv(formuesgrunnlagFlerboligbygning)
                    .filtrer { fastsatt(overfoertFraBarn.norskPersonidentifikator) ikkeEr oppgitt }
                    .forForekomsterAv(useksjonertBoenhet)
                    .filtrer { fastsatt(boligbruk) erLik "primaerbolig" }

                val primarboligForekomster = (primaerbolig + primaerboligIFlerboligbygg).antall

                hvis (harEiendom og (primarboligForekomster erStørreEnn 1)) {
                    betjeningsstrategiSettesTil(
                        ikke,
                        merknadStandard,
                        1
                    )
                }
            }
    }

sakstype(Personlig)
