        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        // Kontrollen gir utslag dersom skattepliktig har oppgitt gjeldsrenter uten at det finnes grunnlag
        // AB-test på skattepliktige som har partall i 7. siffer i partsnummeret
        skattemelding
            .forForekomsterAv(annenGjeld)
            .utslagFor {
                val harVedlegg =
                    forForekomsterAv(paaloepteRenter.beloepUtenHensynTilValgtPrioritertFradragstype).hvorMinstEnForekomst { beloepIValuta har vedlegg }
                hvis(
                    ikke(harVedlegg) og
                        (partsnr.tegnFraIndeks(7).finnesBlant("0", "2", "4", "6", "8")) og
                        (grunnlag(paaloepteRenter.fradragsberettigetBeloep.beloepSomTall) ikkeEr oppgitt) og
                        (fastsatt(paaloepteRenter.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 20_000)
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadDokumentasjon, 1)
                }
            }
    }

