        // kontrollen gir utslag og merknad dersom skattyter har oppgitt tilleggsinformasjon for skattenedsettelse etter ettårsregelen
        // og i sluttdato for utenlandsopphold har oppgitt dato innenfor perioden 01.01-31.12 i inntektsåret
        skattemelding
            .forForekomsterAv(tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen)
            .utslagFor {

                val harKravOmEttaarsregelenMEDSLuttdatoIInntektsaaret = hvorMinstEnForekomst {
                    (sluttdatoForArbeidsoppholdIUtland er oppgitt) og
                        (verdiFor { sluttdatoForArbeidsoppholdIUtland }.dato erStørreEnnEllerLik datoPeriodeStart) og
                        (verdiFor { sluttdatoForArbeidsoppholdIUtland }.dato erMindreEnnEllerLik datoPeriodeSlutt)
                }

                val harVedlegg = sluttdatoForArbeidsoppholdIUtland har vedlegg

                hvis(harKravOmEttaarsregelenMEDSLuttdatoIInntektsaaret og ikke(harVedlegg)) {
                    betjeningsstrategiSettesTil(ikke, merknadDokumentasjon, 1)
                }
            }
    }

