sakstype(Personlig)
    .kontekst(KontrollSaksbehandler) {
        val harTrygdefritakHelsedelOgIkkeSaerskiltSkattepliktISkatteplikt = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .filtrer{personligSkattesubjekt.fritakForTrygdeavgiftsplikt erLik "fritakForHelsedel"}
            .forForekomsterAv(sti(personligSkattesubjekt, saerskiltSkatteplikt))
            .hvorMinstEnForekomst{
                type.finnesIkkeBlant(
                    "kildeskattepliktPaaLoenn",
                    "kildeskattepliktPaaPensjon",
                    "skattepliktSomUtenrikstjenestemann",
                    "skattepliktVedUtenriksoppholdINorskStatstjenesteEllerNato",
                    "skattepliktAvNaeringsdriftEiendomMv"
                )
            }


        val sumLoennTrygd = skattemelding
            .forForekomsterAv(arbeidsgiver)
            .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
            .summer { beloepSomGruppe.beloepSomTall }

        val sumPensjonMv = skattemelding
            .forForekomsterAv(pensjonsinntekt)
            .forForekomsterAv(perMaaned)
            .summer { pensjonsinntektSomGruppe.beloepSomTall }

        val sumDiverseInntekter = (sumLoennTrygd + sumPensjonMv)

        //kontrollen gir utslag dersom skattyter har trygdefritak helsedel for hele året
        //og skattyter IKKE har egenfastsatt trygdefritak (en av delene) på minst en lønn eller en pensjon
        //og sum  lønn, pensjon større enn 54 650
        //skal ikke gi utslag på KPL, KPP, skatteplikt som utenrikstjenestemann eller av næringsdrift, eiendom mv
        skattemelding
            .utslagFor {

                val harOppgittTrygdefriLoenn = forForekomsterAv(arbeidsgiver)
                    .forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (
                            (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftspliktForHelsedel") eller
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel") eller
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriUtenTrygdeavgiftsplikt") eller
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftsplikt ") eller
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForHelsedel ") eller
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForPensjonsdel ") eller
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada  ") eller
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriKildeskattPaaPensjon  ") eller
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigKildeskattPaaPensjon  ") eller
                                (fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftsplikt")
                            ) og
                            (
                                ikke(
                                    grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) erLik fastsatt(
                                        beloepSomGruppe.trygdeEllerSkattemessigUnntak
                                    )
                                )
                                )
                    }

                val harOppgittTrygdefriPensjon = forForekomsterAv(pensjonsinntekt)
                    .forForekomsterAv(perMaaned)
                    .hvorMinstEnForekomst {
                        (
                            (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftspliktForHelsedel") eller
                                (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftspliktForPensjonsdel") eller
                                (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriUtenTrygdeavgiftsplikt") eller
                                (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftsplikt") eller
                                (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForHelsedel") eller
                                (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForPensjonsdel") eller
                                (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada") eller
                                (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattefriKildeskattPaaPensjon") eller
                                (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigKildeskattPaaPensjon") eller
                                (fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik "skattepliktigUtenTrygdeavgiftsplikt")
                            ) og
                            (
                                ikke(
                                    grunnlag(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) erLik fastsatt(
                                        pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak
                                    )
                                )
                                )
                    }


                hvis(
                    harTrygdefritakHelsedelOgIkkeSaerskiltSkattepliktISkatteplikt og
                        (ikke(harOppgittTrygdefriLoenn) og ikke(harOppgittTrygdefriPensjon)) og
                        (sumDiverseInntekter erStørreEnnEllerLik 54_650)
                )
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har oppgitt trygde- eller skattemessig unntak på lønn eller pensjon
        // men ikke har lagt til endringsårsak fritakDelvisFritakForTrygdeavgift eller opphoerTrygdemedlemskap på lønn eller pensjon
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(gruppe(arbeidsgiver, pensjonsinntekt))
            .filtrer {
                forForekomsterAv(aarsakTilEndring)
                    .harIngenForekomsterDer {
                        fastsatt(aarsak).finnesBlant(
                            "fritakDelvisFritakForTrygdeavgift",
                            "opphoerTrygdemedlemskap"
                        )
                    }
            }
            .utslagFor {
                val loennSomErOppgittMedTrygdemessigUnntak =
                    forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                        .filtrer { fastsatt(beloepSomGruppe.beloepSomTall) er oppgitt }
                        .hvorMinstEnForekomst {
                            (
                                fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                    "skattepliktigUtenTrygdeavgiftsplikt",
                                    "skattefriUtenTrygdeavgiftsplikt"
                                )
                                ) og
                                (grunnlag(beloepSomGruppe.trygdeEllerSkattemessigUnntak) ikkeEr oppgitt)
                        }

                val pensjonSomErOppgittMedTrygdemessigUnntak = forForekomsterAv(perMaaned)
                    .filtrer { fastsatt(pensjonsinntektSomGruppe.beloepSomTall) er oppgitt }
                    .hvorMinstEnForekomst {
                        (
                            fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                                "skattepliktigUtenTrygdeavgiftsplikt",
                                "skattefriUtenTrygdeavgiftsplikt"
                            )
                            ) og
                            (grunnlag(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak) ikkeEr oppgitt)
                    }

                hvis(ikke(harInnsending) og loennSomErOppgittMedTrygdemessigUnntak eller pensjonSomErOppgittMedTrygdemessigUnntak) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

