sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har oppgitt egenfastsatt tap på verdipapirfond
        // kontrollen gir merknad i SME
        // Skal kun gi merknad i SME fra 50 000 til 5 mill (for å unngå flere merknader på samme beløp)
        skattemelding
            .forForekomsterAv(verdipapirfond)
            .utslagFor {
                val harDifferanseTapAksjedel = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigTapVedRealisasjonAvAndelIAksjedel.fradragsberettigetBeloep.beloepSomTall) erLik 0) og
                        (
                            (
                                fastsatt(skattepliktigTapVedRealisasjonAvAndelIAksjedel.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    skattepliktigTapVedRealisasjonAvAndelIAksjedel.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erStørreEnnEllerLik 50_000
                            ) og
                        (
                            (
                                fastsatt(skattepliktigTapVedRealisasjonAvAndelIAksjedel.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    skattepliktigTapVedRealisasjonAvAndelIAksjedel.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erMindreEnn 5_000_000
                            )
                }

                val harDifferanseTapRentedel = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigTapVedRealisasjonAvAndelIRentedel.fradragsberettigetBeloep.beloepSomTall) erLik 0) og
                        (
                            (
                                fastsatt(skattepliktigTapVedRealisasjonAvAndelIRentedel.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    skattepliktigTapVedRealisasjonAvAndelIRentedel.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erStørreEnnEllerLik 50_000
                            ) og
                        (
                            (
                                fastsatt(skattepliktigTapVedRealisasjonAvAndelIRentedel.fradragsberettigetBeloep.beloepSomTall) - grunnlag(
                                    skattepliktigTapVedRealisasjonAvAndelIRentedel.fradragsberettigetBeloep.beloepSomTall
                                )
                                ) erMindreEnn 5_000_000
                            )
                }

                val harVedleggAksjedel =
                    forForekomsterAv(skattepliktigTapVedRealisasjonAvAndelIAksjedel.beloepUtenHensynTilValgtPrioritertFradragstype).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                val harVedleggRentedel =
                    forForekomsterAv(skattepliktigTapVedRealisasjonAvAndelIRentedel.beloepUtenHensynTilValgtPrioritertFradragstype).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                hvis(
                    ikke(harInnsending) og (ikke(harVedleggAksjedel) og harDifferanseTapAksjedel) eller
                        (ikke(harVedleggRentedel) og harDifferanseTapRentedel)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har redusert gevinst på verdipapirfond
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(verdipapirfond)
            .filtrer {
                (grunnlag(skattepliktigGevinstVedRealisasjonAvAndelIAksjedel.beloepSomTall) er oppgitt) eller (
                    grunnlag(
                        skattepliktigGevinstVedRealisasjonAvAndelIRentedel.beloepSomTall
                    ) er oppgitt
                    )
            }
            .utslagFor {
                val differanseGevinstAksjedel =
                    (
                        grunnlag(skattepliktigGevinstVedRealisasjonAvAndelIAksjedel.beloepSomTall) - fastsatt(
                            skattepliktigGevinstVedRealisasjonAvAndelIAksjedel.beloepSomTall
                        )
                        )
                val differanseGevinstRentedel =
                    (
                        grunnlag(skattepliktigGevinstVedRealisasjonAvAndelIRentedel.beloepSomTall) - fastsatt(
                            skattepliktigGevinstVedRealisasjonAvAndelIRentedel.beloepSomTall
                        )
                        )
                val vedleggAksjedel =
                    skattepliktigGevinstVedRealisasjonAvAndelIAksjedel.beloepIValuta har vedlegg
                val vedleggRentedel =
                    skattepliktigGevinstVedRealisasjonAvAndelIRentedel.beloepIValuta har vedlegg
                val harVedlegg = vedleggAksjedel eller vedleggRentedel

                // Skal kun gi merknad i SME fra 50 000 til 5 mill (for å unngå flere merknader på samme beløp)
                hvis(
                    ikke(harInnsending) og
                        ikke(harVedlegg) og
                        ((differanseGevinstAksjedel + differanseGevinstRentedel) erStørreEnnEllerLik 50_000) og ((differanseGevinstAksjedel + differanseGevinstRentedel) erMindreEnn 5_000_000)
                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon, 1
                    )
                }
            }
    }

