sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har redusert renteinntekt på verdipapirfond eller obligasjon
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(gruppe(verdipapirfond, obligasjonOgSertifikat))
            .filtrer {
                (grunnlag(skattepliktigRenteinntekt.beloepSomTall) er oppgitt) eller (
                    grunnlag(
                        renteinntektAvObligasjonOgSertifikatSomGruppe.beloepSomTall
                    ) er oppgitt
                    )
            }
            .utslagFor {
                val differanseRenteinntektVerdipapirfond =
                    (grunnlag(skattepliktigRenteinntekt.beloepSomTall) - fastsatt(skattepliktigRenteinntekt.beloepSomTall))
                val differanseRenteinntektObligasjon =
                    (
                        grunnlag(renteinntektAvObligasjonOgSertifikatSomGruppe.beloepSomTall) - fastsatt(
                            renteinntektAvObligasjonOgSertifikatSomGruppe.beloepSomTall
                        )
                        )
                val harVedleggRenteinntekt =
                    forForekomsterAv(skattepliktigRenteinntekt).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                val harVedleggObligasjon =
                    forForekomsterAv(renteinntektAvObligasjonOgSertifikatSomGruppe).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                val harVedlegg = harVedleggObligasjon eller harVedleggRenteinntekt

                hvis(ikke(harInnsending) og ikke(harVedlegg) og ((differanseRenteinntektVerdipapirfond + differanseRenteinntektObligasjon) erStørreEnnEllerLik 50_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadDokumentasjon, 1
                    )
                }
            }
    }

