sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag når skattepliktige egenfastsetter underkort "Virtuell valuta og kryptovaluta" uten beløp i noen av feltene, men med vedlegg
        skattemelding
              .forForekomsterAv(virtuellEiendel)
            .utslagFor {
                val harVedlegg = hvorMinstEnForekomst { gjelderFlereVirtuelleEiendeler har vedlegg }
                val ikkeHarBeloep = forAlleForekomster
                      .hvorMinstEnForekomst { beloepSomTall erUlik 0 }
                      .ikke()

                hvis(
                    ikkeHarBeloep og harVedlegg
                ) {
                     betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .verdiFor { partsnummerSomTekst }

        val harFordringFjoraaret = fjoraaretsSkattemelding
            .forForekomsterAv(inntektsaar2021.annenFordring)
            .hvorMinstEnForekomst {
                    (fastsatt(inntektsaar2021.opptjenteRenter.beloepSomTall) erStørreEnn 0) eller 
                    (fastsatt(inntektsaar2021.formuesverdi.beloepSomTall) erStørreEnn 0)
            }

        // AB-test: kontrollen gir utslag dersom skattyter hadde fordring i fjor, men ikke har oppgitt noen fordring i år,
        // på skattytere som har tallene 0, 3 eller 6 i 7.siffer i partsnummeret
        // kontrollen gir merknad i SME
        skattemelding 
           .utslagFor {

               val harFordringIAar = forForekomsterAv(annenFordring)
                  .hvorMinstEnForekomst {
                        (fastsatt(opptjenteRenter.beloepSomTall) erStørreEnn 0) eller 
                        (fastsatt(formuesverdi.beloepSomTall) erStørreEnn 0)}

           hvis(ikke(harFordringIAar) og
                (partsnr.tegnFraIndeks(7).finnesBlant("0", "3", "6")) og
                harFordringFjoraaret)
             {
                betjeningsstrategiSettesTil(ikke, merknadTips)
             }
           }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        val partsnr = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
