            .hvorMinstEnForekomst {
                    (fastsatt(inntektsaar2021.opptjenteRenter.beloepSomTall) erStørreEnn 0) eller 
                    (fastsatt(inntektsaar2021.formuesverdi.beloepSomTall) erStørreEnn 0)
            }

        // AB-test: kontrollen gir utslag dersom skattyter hadde fordring i fjor, men ikke har oppgitt noen fordring i år,
        // på skattytere som har tallene 1, 4 eller 7 i 7.siffer i partsnummeret
        // kontrollen gir merknad i SME
        skattemelding 
           .utslagFor {

               val harFordringIAar = forForekomsterAv(annenFordring)
                  .hvorMinstEnForekomst {
                        (fastsatt(opptjenteRenter.beloepSomTall) erStørreEnn 0) eller 
                        (fastsatt(formuesverdi.beloepSomTall) erStørreEnn 0)}

           hvis(ikke(harFordringIAar) og
                (partsnr.tegnFraIndeks(7).finnesBlant("1", "4", "7")) og
                harFordringFjoraaret)
             {
                betjeningsstrategiSettesTil(ikke, merknadTips, skjulerSkatteberegning)
             }
           }
    }

sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {
        val loennUtenDobbeltbeskatningsmetode = skattemelding
            .forForekomsterAv(sti(arbeidsgiver, samledeYtelserFraArbeidsgiverPerBehandlingsart))
            .harIngenForekomsterDer { fastsatt(beloepSomGruppe.metodeVedDobbeltbeskatning) erLik "denAlternativeFordelingsmetoden" }

        val pensjonUtenDobbeltbeskatningsmetode = skattemelding
            .forForekomsterAv(sti(pensjonsinntekt, perMaaned))
            .harIngenForekomsterDer { fastsatt(pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning) erLik "denAlternativeFordelingsmetoden" }

        // Kontrollen gir utslag og merknad dersom skattyter har oppgitt tilleggsinformasjon for den alternative fordelingsmetoden
        // og IKKE har oppgitt metode for dobbelbeskatning = den alternative fordelingsmetoden på lønn eller pensjon
        // merknaden skal kun vises på tilleggsinformasjonskortet og ikke på lønn og pensjons kortene.
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(tilleggsinformasjonVedKravOmSkattenedsettelseEtterDenAlternativeFordelingsmetoden)
            .utslagFor {

                val harKravOmDenAlternativeFordelingsmetoden = hvorMinstEnForekomst {
                    (startdatoForOppholdIUtland er oppgitt) eller
                        (sluttdatoForOppholdIUtland er oppgitt)
                }

                hvis(
                    harKravOmDenAlternativeFordelingsmetoden og
                        loennUtenDobbeltbeskatningsmetode og pensjonUtenDobbeltbeskatningsmetode
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {
        val loennUtenDobbeltbeskatningsmetode = skattemelding
            .forForekomsterAv(sti(arbeidsgiver, samledeYtelserFraArbeidsgiverPerBehandlingsart))
            .harIngenForekomsterDer { fastsatt(beloepSomGruppe.metodeVedDobbeltbeskatning) erLik "ettaarsregelen" }

        val pensjonUtenDobbeltbeskatningsmetode = skattemelding
            .forForekomsterAv(sti(pensjonsinntekt, perMaaned))
            .harIngenForekomsterDer { fastsatt(pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning) erLik "ettaarsregelen" }

        // kontrollen gir utslag og merknad dersom skattyter har oppgitt tilleggsinformasjon for skattenedsettelse etter ettårsregelen
        // og IKKE har oppgitt metode for dobbelbeskatning = ettårsregelen på lønn eller pensjon
        // merknaden skal kun vises på tilleggsinformasjonskortet og ikke på lønn og pensjons kortene.
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(tilleggsinformasjonVedKravOmSkattenedsettelseEtterEttaarsregelen)
            .utslagFor {

                val harKravOmEttaarsregelen = hvorMinstEnForekomst {
                    (startdatoForArbeidsoppholdIUtland er oppgitt) eller
                        (sluttdatoForArbeidsoppholdIUtland er oppgitt) eller
                        (antallOppholdsdagerINorge er oppgitt)
                }

                hvis(
                    harKravOmEttaarsregelen og
                        loennUtenDobbeltbeskatningsmetode og pensjonUtenDobbeltbeskatningsmetode
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig) 
     .kontekst(ValideringSkattepliktig) {

            val bruttoLoennUtgiftsgodtgjoerelseMv = skattemelding
                       .forForekomsterAv(summertSkattegrunnlagForVisning)
                       .forForekomsterAv(
                           gruppe(
                               samletLoennsinntektMedTrygdeavgiftspliktOgMedTrekkplikt_SKVIS,
                               samletLoennsinntektUtenTrygdeavgiftspliktOgMedTrekkplikt_SKVIS,
                               samletOverskuddPaaUtgiftsgodtgjoerelse_SKVIS,
                               annenArbeidsinntekt_SKVIS,
                               inntektsfradragAvRentefordelPaaLaanIArbeidsforhold_SKVIS,
                               arbeidsavklaringspenger_SKVIS,
                               avkortetFordelVedElektroniskKommunikasjon_SKVIS,
                               overskuddAvUtgiftsgodtgjoerelseVedSmusstillegg_SKVIS,
                               overgangsstoenadTilEnsligForelder_SKVIS,
                               kvalifiseringsstoenad_SKVIS,
                               styrehonorarFraNorskSelskapTilSkattepliktigBosattIUtlandet_SKVIS,
                               arbeidsinntektFraKompensasjonsytelseUtbetaltAvNav_SKVIS
                   )
               )
               .summer { fastsatt(beloep_SKVIS) }

      // Andre kapitalkostnader (Sum begge felt) > (40% av brutto lønn). I tillegg må fradraget overstige 100 000
        skattemelding
            .forForekomsterAv(annenKapitalkostnad)
            .utslagFor {
               val sumAndreKapitalkostnader =
                   summer { paaloepteRenterFraInkassoselskap.fradragsberettigetBeloep.beloepSomTall +
                               utgiftVedRefinansieringAvLaan.fradragsberettigetBeloep.beloepSomTall }

              val harVedleggRenterFraInkassoselskap =
                    forForekomsterAv (fastsatt(paaloepteRenterFraInkassoselskap.beloepUtenHensynTilValgtPrioritertFradragstype)).hvorMinstEnForekomst { fastsatt(beloepIValuta) har vedlegg }

               val harVedleggRefinansiering =
                    forForekomsterAv(fastsatt(utgiftVedRefinansieringAvLaan.beloepUtenHensynTilValgtPrioritertFradragstype)).hvorMinstEnForekomst { fastsatt(beloepIValuta) har vedlegg }

               val harRenterFraInkassoselskap = hvorMinstEnForekomst {
                        fastsatt(paaloepteRenterFraInkassoselskap.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 0
                   }

               val harUtgiftVedRefinansiering = hvorMinstEnForekomst {
                        fastsatt(utgiftVedRefinansieringAvLaan.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 0
                   }

                   hvis( ((ikke(harVedleggRenterFraInkassoselskap) og harRenterFraInkassoselskap) eller
                              (ikke(harVedleggRefinansiering) og harUtgiftVedRefinansiering)) og
                               (sumAndreKapitalkostnader erStørreEnn 100_000) og
                               (sumAndreKapitalkostnader erStørreEnn (bruttoLoennUtgiftsgodtgjoerelseMv * 40.prosent))
                        )
                 {
                    betjeningsstrategiSettesTil(ikke, merknadDokumentasjon)
                }
            }
    }

sakstype(Personlig)
     .kontekst(ValideringSkattepliktig) {

    //Kontrollen slår ut når skattepliktige krever fradrag for kostnader (> 1mill) knyttet til refinansiering av lån i kort for "andre finansprodukter"
         skattemelding
             .forForekomsterAv(annenKapitalkostnad)
             .utslagFor {
                 val harVedlegg =
                     forForekomsterAv(utgiftVedRefinansieringAvLaan.beloepUtenHensynTilValgtPrioritertFradragstype).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                 hvis(
                    ikke(harVedlegg) og
                         ((grunnlag(utgiftVedRefinansieringAvLaan.fradragsberettigetBeloep.beloepSomTall) ikkeEr oppgitt) og
                         (fastsatt(utgiftVedRefinansieringAvLaan.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 1_000_000))
                 ) {
                     betjeningsstrategiSettesTil(ikke, merknadDokumentasjon, 1)
                 }
             }
     }

sakstype(Personlig)
     .kontekst(ValideringSkattepliktig) {
        val harUtslagPaaKontroll = utslagPåKontroll("ANDRE_KAPITALKOSTNADER_KALKYLEDULT_HØYT_FRADRAG_VS_INNTEKT_FORMUE")

    //Kontrollen slår ut når skattepliktige krever fradrag for kostnader over kr 30 000 knyttet til refinansiering av lån i kort for "andre finansprodukter"
    //ikke utslag når utslag på kontroll "ANDRE_KAPITALKOSTNADER_KALKYLEDULT_HØYT_FRADRAG_VS_INNTEKT/FORMUE"
         skattemelding
             .forForekomsterAv(annenKapitalkostnad)
             .utslagFor {
                 val harVedlegg =
                     forForekomsterAv(utgiftVedRefinansieringAvLaan.beloepUtenHensynTilValgtPrioritertFradragstype).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                 hvis(
                    ikke(harVedlegg) og ikke(harUtslagPaaKontroll) og 
                         ((grunnlag(utgiftVedRefinansieringAvLaan.fradragsberettigetBeloep.beloepSomTall) ikkeEr oppgitt) og
                         (fastsatt(utgiftVedRefinansieringAvLaan.fradragsberettigetBeloep.beloepSomTall) erStørreEnn 30_000))
                 ) {
                     betjeningsstrategiSettesTil(ikke, merknadDokumentasjon, 1)
                 }
             }
     }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val harUtslagPaaKontroll = utslagPåKontroll("ANDRE_KAPITALKOSTNADER_KALKYLEDULT_HØYT_FRADRAG_VS_INNTEKT_FORMUE")

      // Kontrollen gir utslag dersom skattyter har oppgitt Rentekostnader til inkassoselskap på mer enn 30 000
      // kke haer utslag på kontroll "NY_ANDRE_KAPITALKOSTNADER_KALKYLEDULT_HØYT_FRADRAG_VS_INNTEKT_FORMUE
        skattemelding
            .forForekomsterAv(annenKapitalkostnad)
            .utslagFor {
                val differansePaaloepteRenterFraInkassoselskap =
