    .kontekst(ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt/fastsatt gevinst på aksjesparekonto er midre enn grunnlag
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(aksjesparekonto)
            .filtrer { grunnlag(skattepliktigGevinstVedRealisasjonSomGruppe.beloepSomTall) er oppgitt }
            .utslagFor {
                val differanseGevinst = (
                    grunnlag(skattepliktigGevinstVedRealisasjonSomGruppe.beloepSomTall) - fastsatt(
                        skattepliktigGevinstVedRealisasjonSomGruppe.beloepSomTall
                    )
                    )

                val harVedlegg =
                    forForekomsterAv(skattepliktigGevinstVedRealisasjonSomGruppe).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                // Skal kun gi merknad i SME fra 1 000 til 20 mill (for å unngå flere merknader/oppgaver på samme beløp)
                hvis(ikke(harVedlegg) og ((differanseGevinst erStørreEnnEllerLik 1_000) og (differanseGevinst erMindreEnn 20_000_000))) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt/fastsatt formue på aksjesparekonto er mindre enn grunnlag
        skattemelding
            .forForekomsterAv(aksjesparekonto)
            .filtrer {
                (grunnlag(verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall) er oppgitt) eller (
                    grunnlag(
                        formuesverdiForKontanter.beloepSomTall
                    ) er oppgitt
                    )
            }
            .utslagFor {
                val differanseFormueFoerVerdsettingsrabatt =
                    (
                        grunnlag(verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall) - fastsatt(
                            verdiFoerVerdsettingsrabattForAksjedel.beloepSomTall
                        )
                        )
                val differanseFormueKontanter =
                    (grunnlag(formuesverdiForKontanter.beloepSomTall) - fastsatt(formuesverdiForKontanter.beloepSomTall))

                val harVedlegg =
                    forForekomsterAv(verdiFoerVerdsettingsrabattForAksjeOgAksjefond).hvorMinstEnForekomst { beloepIValuta har vedlegg } eller
                        forForekomsterAv(formuesverdiForKontanter).hvorMinstEnForekomst { beloepIValuta har vedlegg }

                // Skal kun gi merknad i SME fra 500 000(for å unngå flere merknader/oppgaver på samme beløp)
                hvis(
                    ikke(harVedlegg) og
                        ((differanseFormueFoerVerdsettingsrabatt + differanseFormueKontanter) erStørreEnnEllerLik 500_000)

                ) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1,

                        )
                }
            }
    }

sakstype(Personlig)
    .kontekst(KontrollSaksbehandler, ValideringSkattepliktig) {
        // Gir utslag dersom oppgitt tap på aksjesparekonto er ulik grunnlag med 20 mill eller mer
        skattemelding
            .forForekomsterAv(aksjesparekonto)
            .forForekomsterAv(sti(skattepliktigTapVedRealisasjonSomGruppe, fradragsberettigetBeloep))
            .utslagFor {
                val differanseTap = differanse(
                    fastsatt(beloepSomTall),
                    grunnlag(beloepSomTall)
                )

                hvis(differanseTap erStørreEnnEllerLik 20_000_000) {
                    betjeningsstrategiSettesTil(
                        myndighetsfastsettingMedVarselOgVedtak, merknadStandard, 1,
                        Gruppering settesTil Finans,
                        spesialomraade,
                        Avviksbeloep settesTil differanseTap
                    )
                }
            }
    }

