                val sumPensjon = forForekomsterAv(perMaaned)
                    .summer { fastsatt(pensjonsinntektSomGruppe.beloepSomTall) }

                val harIngenForekomstAvPensjonMedTrygdefritak = forForekomsterAv(perMaaned)
                   .harIngenForekomsterDer{fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak).finnesBlant(
                            "skattepliktigUtenTrygdeavgiftspliktForHelsedel",
