sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // Kontrollen gir utslag dersom skattyter har oppgitt gjeldsrenter som er høyere enn 10% av gjelden (skal ikke gi utslag dersom alt er preutfylt)
        // Gir merknad
        skattemelding
            .forForekomsterAv(konto)
            .filtrer { fastsatt(andelAvGjeld.beloepSomTall) erStørreEnn 50_000 }
            .utslagFor {

                hvis(ikke(harInnsending) og 
                    (
                        fastsatt(andelAvPaaloepteRenter.beloepSomTall) erStørreEnn grunnlag(
                            andelAvPaaloepteRenter.beloepSomTall
                        )
                        ) og
                        (
                            fastsatt(andelAvPaaloepteRenter.beloepSomTall) erStørreEnn (
                                fastsatt(
                                    andelAvGjeld.beloepSomTall
                                ) * 0.1
                                )
                            ) og
                        (fastsatt(andelAvPaaloepteRenter.beloepSomTall) erMindreEnnEllerLik 1_000_000) 
                ) {
                    betjeningsstrategiSettesTil(ikke, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        val sumEierandelAlleForekomsterGrunnlag = skattemelding
          .forForekomsterAv(fastEiendom)
          .summer{grunnlag(eierandel)}

        val sumEierandelAlleForekomsterFastsatt = skattemelding
          .forForekomsterAv(fastEiendom)
          .summer{fastsatt(eierandel)}  

        val diffSumEierandel = differanse(sumEierandelAlleForekomsterGrunnlag, sumEierandelAlleForekomsterFastsatt)  

        // kontrollen gir utslag og tips dersom skattyter har redusert eierandel og realisasjon av eiendom ikke er lagt inn
        // for å unngå at den gir utslag dersom det bare fjernet en forekomst som lå i utlast og lagt til en tilsvarende i fastsatt,
        // legges det til et ekstrakriterie at denne kun skal gi utslag dersom det er diff i summen av eierandel på alle forekomster
        skattemelding
          .utslagFor{
            val harEndretEierandel = forForekomsterAv(fastEiendom)
               .hvorMinstEnForekomst{grunnlag(eierandel) erStørreEnn fastsatt(eierandel)}

            hvis(ikke(harInnsending) og 
                 harEndretEierandel og
                 ikke(realisasjonAvFastEiendom er oppgitt) og
                 (diffSumEierandel erStørreEnn 0)) 
            {
              betjeningsstrategiSettesTil(ikke, merknadTips, 1)
            }
          } 
    }

