           .forForekomsterAv { metadata }
           .hvorMinstEnForekomst {type.finnesBlant("UTLEIE_AV_FAST_EIENDOM", "UTLEIE_SEKUNDAERBOLIGMODELL") }        

        // Kontrollen gir utslag dersom skattyter hadde utleie i fjor, men ikke har oppgitt noen utleie i år,
        // Skal ikke gi utslag hvis skattyyter har fått produsert utvidet veileding (og vist det eller et tilhørende tips) 
         val harUtleidFjoraaret = fjoraaretsSkattemelding
             .forForekomsterAv(
                 gruppe(
                     inntektsaar2021.sjablongbehandletUtleie,
                     inntektsaar2021.regnskapsbehandletUtleie,
                     inntektsaar2021.sjablongOgRegnskapsbehandletUtleie))
             .hvorMinstEnForekomst {
                 ((fastsatt(inntektsaar2021.bruttoLeieinntektVedSjablongbehandletUtleie.beloepSomTall) erStørreEnn 0) eller 
                  (fastsatt(inntektsaar2021.bruttoLeieinntektVedRegnskapsbehandletUtleie.beloepSomTall) erStørreEnn 0)) }

         skattemelding
            .utslagFor{

              val harUtleie = forForekomsterAv(gruppe(sjablongbehandletUtleie,
                                                      regnskapsbehandletUtleie,
                                                      sjablongOgRegnskapsbehandletUtleie))
                   .hvorMinstEnForekomst {(fastsatt(bruttoLeieinntektVedSjablongbehandletUtleie.beloepSomTall) erStørreEnn 0) eller 
                                          (fastsatt(bruttoLeieinntektVedRegnskapsbehandletUtleie.beloepSomTall) erStørreEnn 0)}

              hvis(ikke(harUtvidetVeiledningPaaUtleie) og
                   ikke(harUtleie) og 
                   harUtleidFjoraaret) 
              {
                betjeningsstrategiSettesTil(ikke, merknadTips, skjulerSkatteberegning)
              } 
            } 
    }

