            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        //Kontrollen gir utslag dersom skattyter har valgt endringsårsak = skattemessigEmigrasjon
        //og skattyter har oppgitt dobbeltbeskatningsmetode og IKKE satt til skattefri
        //kontrollen skal ikke gi utslag dersom skatteplikt er begrenset
        skattemelding
            .forAlleForekomster
            .utslagFor {
                val kortMedEndringsaarsakOgMetodeIkkeSkattefri = hvorMinstEnForekomst { 
                        forForekomsterAv(aarsakTilEndring).hvorMinstEnForekomst{aarsak erLik "skattemessigEmigrasjon" } og
                        forAlleForekomster.hvorMinstEnForekomst {
                          (fastsatt(metodeVedDobbeltbeskatning) er oppgitt) og
                          (fastsatt(trygdeEllerSkattemessigUnntak).finnesIkkeBlant( 
