sakstype(Personlig)
    .kontekst(VeiledningSaksbehandler, ValideringSkattepliktig) {

       val harUtslagPaaKontroll = utslagPåKontroll("EMIGRASJON_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD") eller
                                   utslagPåKontroll("AVTALEBOSTED_ÅRSAKSKODE_UGYLDIG_KOMBINASJON_METODE_MERKNAD")


        val harBegrensetSkatteplikt = skattepliktGjeldende
            .forForekomsterAv(skatteplikt)
            .hvorMinstEnForekomst { personligSkattesubjekt.skattepliktTilNorge erLik "begrenset" }

        val loennMedDobbeltbeskatningsmetodeUtenLand = skattemelding
            .forForekomsterAv(arbeidsgiver)
            .hvorMinstEnForekomst {
                val loennUtenland = landkode ikkeEr oppgitt
                val loennMedMetode = forForekomsterAv(samledeYtelserFraArbeidsgiverPerBehandlingsart)
                    .hvorMinstEnForekomst {
                        (fastsatt(beloepSomGruppe.metodeVedDobbeltbeskatning) er oppgitt) og
                            (
                                fastsatt(beloepSomGruppe.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriKildeskattPaaPensjon",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                    "skattefriUtenTrygdeavgiftsplikt"
                                )
                                )
                    }
                loennUtenland og loennMedMetode
            }

        val pensjonMedDobbeltbeskatningsmetodeUtenLand = skattemelding
            .forForekomsterAv(pensjonsinntekt)
            .hvorMinstEnForekomst {
                val pensjonUtenLand = landkode ikkeEr oppgitt
                val pensjonMedMetode = forForekomsterAv(perMaaned)
                    .hvorMinstEnForekomst {
                        (fastsatt(pensjonsinntektSomGruppe.metodeVedDobbeltbeskatning) er oppgitt) og
                            (
                                fastsatt(pensjonsinntektSomGruppe.trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriKildeskattPaaPensjon",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                    "skattefriUtenTrygdeavgiftsplikt"
                                )
                                )
                    }
                pensjonUtenLand og pensjonMedMetode
            }

        val andreKortMedDobbeltbeskatningsmetodeUtenLand = skattemelding
            .forForekomsterAv(
                gruppe(
                    konto,
                    aksjeIkkeIAksjonaerregisteret,
                    aksjeIAksjonaerregisteret,
                    verdipapirfond,
                    obligasjonOgSertifikat,
                    aksjesparekonto,
                    annetFinansprodukt,
                    skattefavorisertIndividuellSparingTilPensjon,
                    annenKapitalinntekt
                )
            )
            .hvorMinstEnForekomst {
                val kortUtenland = landkode ikkeEr oppgitt

                val feltMedMetode = forAlleForekomster
                    .hvorMinstEnForekomst {
                        (fastsatt(metodeVedDobbeltbeskatning) er oppgitt) og
                            (
                                fastsatt(trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                                    "skattefriMedTrygdeavgiftspliktForHelsedel",
                                    "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                                    "skattefriMedTrygdeavgiftsplikt",
                                    "skattefriKildeskattPaaPensjon",
                                    "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                                    "skattefriUtenTrygdeavgiftsplikt"
                                )
                                )
                    }

                kortUtenland og feltMedMetode
            }

        val eiendomMedlandkode = skattemelding
            .forForekomsterAv(fastEiendom)
            .filtrer { fastsatt(landkode) er oppgitt }
            .verdierFor { internEiendomsidentifikator }

        val realisasjonAvFastEiendomMedDobbeltbeskatningsmetodeUtenLand = skattemelding
            .forForekomsterAv(realisasjonAvFastEiendom)
            .hvorMinstEnForekomst {
                (fastsatt(gevinstVedRealisasjonAvFastEiendom.metodeVedDobbeltbeskatning) er oppgitt) og
                    (
                        fastsatt(trygdeEllerSkattemessigUnntak).finnesIkkeBlant(
                            "skattefriMedTrygdeavgiftspliktForHelsedel",
                            "skattefriMedTrygdeavgiftspliktForPensjonsdel",
                            "skattefriMedTrygdeavgiftsplikt",
                            "skattefriKildeskattPaaPensjon",
                            "skattefriMedTrygdeavgiftspliktForSykestoenadsdelForArbeidstakereUtsendtFraUSAOgCanada",
                            "skattefriUtenTrygdeavgiftsplikt"
                        )
                        ) og
                    (fastsatt(internEiendomsidentifikator).finnesIkkeBlant(eiendomMedlandkode))
            }

        //kontrollen gir utslag dersom det er valgt metode for å unngå dobbeltbeskatning, men det ikke er oppgitt landkode
        //skal ikke gi utslag dersom det er oppgitt skattefritt unntak
        //skal IKKE gi utslag dersom skattyter har begrenset skatteplikt
        //Skalikke gi utslag dersom utslag på gitte kontroller
        skattemelding
            .utslagFor {
                hvis(ikke(harUtslagPaaKontroll) og
                    ikke(harBegrensetSkatteplikt) og
                        (
                            loennMedDobbeltbeskatningsmetodeUtenLand eller
                                pensjonMedDobbeltbeskatningsmetodeUtenLand eller
                                realisasjonAvFastEiendomMedDobbeltbeskatningsmetodeUtenLand eller
                                andreKortMedDobbeltbeskatningsmetodeUtenLand
                            )
                ) {
                    betjeningsstrategiSettesTil(smiaMerknadStandard, merknadStandard, 1)
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
