        // Gir merknad i SME
        skattemelding
            .forForekomsterAv(fondskonto)
            .utslagFor {
                val differanseFormueVerdi =
                    differanse(
                        grunnlag(verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloepSomTall),
                        fastsatt(verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloepSomTall)
                    )
                val differanseFormueKontanter = differanse(
                    grunnlag(formuesverdiForKontanterMv.beloepSomTall),
                    fastsatt(formuesverdiForKontanterMv.beloepSomTall)
                )

                hvis(ikke(harInnsending) og ((differanseFormueVerdi + differanseFormueKontanter) erStørreEnnEllerLik 10_000_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

