                        fastsatt(skattepliktigGevinstVedRealisasjonAvOgUttakFraRentedelSomGruppe.beloepSomTall)
                    )

                hvis(ikke(harInnsending) og ((differanseGevinstAksjedel + differanseGevinstRentedel) erStørreEnnEllerLik 2_000_000)) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har redusert formue på fondskonto
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(fondskonto)
            .filtrer {
                (grunnlag(verdiFoerVerdsettingsrabattForAksjeOgAksjefond.beloepSomTall) er oppgitt) eller (
                    grunnlag(
                        formuesverdiForKontanterMv.beloepSomTall
                    ) er oppgitt
                    )
            }
            .utslagFor {
                val differanseFormueVerdi =
                    (
