                gruppe(
                    sti(skattepliktigTapVedRealisasjonAvOgUttakFraAksjedelSomGruppe, fradragsberettigetBeloep),
                    sti(skattepliktigTapVedRealisasjonAvOgUttakFraRentedelSomGruppe, fradragsberettigetBeloep)
                )
            )
            .utslagFor {
                val differanseTap = differanse(fastsatt(beloepSomTall), grunnlag(beloepSomTall))

                hvis(differanseTap erStørreEnnEllerLik 2_000_000)
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard, 1
                    )
                }
            }
    }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {
        // kontrollen gir utslag dersom skattyter har lagt til eller økt tap på fondskonto på 50 000 eller mer
        // kontrollen gir merknad i SME
        skattemelding
            .forForekomsterAv(fondskonto)
            .utslagFor {
                val differanseTapAksjedel =
                    hvorMinstEnForekomst {
                        (
                            fastsatt(skattepliktigTapVedRealisasjonAvOgUttakFraAksjedelSomGruppe.fradragsberettigetBeloep.beloepSomTall) -
                                grunnlag(skattepliktigTapVedRealisasjonAvOgUttakFraAksjedelSomGruppe.fradragsberettigetBeloep.beloepSomTall)
                            ) erStørreEnnEllerLik 50_000
                    }

                val differanseTapRentedel =
                    hvorMinstEnForekomst {
                        (
                            fastsatt(skattepliktigTapVedRealisasjonAvOgUttakFraRentedelSomGruppe.fradragsberettigetBeloep.beloepSomTall) -
                                grunnlag(skattepliktigTapVedRealisasjonAvOgUttakFraRentedelSomGruppe.fradragsberettigetBeloep.beloepSomTall)
                            ) erStørreEnnEllerLik 50_000
                    }

                val harDifferanseTap = differanseTapAksjedel eller differanseTapRentedel

                val harVedleggAksjedel =
                    forForekomsterAv(skattepliktigTapVedRealisasjonAvOgUttakFraAksjedelSomGruppe.beloepUtenHensynTilValgtPrioritertFradragstype)
                        .hvorMinstEnForekomst { beloepSomTall har vedlegg }
                val harVedleggRentedel =
