            .forForekomsterAv(reisefradrag_SKVIS)
            .summer{fastsatt(beloep_SKVIS)}


        // Kontrollen gir utslag dersom skattepliktig har krevd fradrag for arbeidsreiser
        // med 100 reisedager eller mer, og/eller 250 km tur retur eller mer
        // og har bom- og fergekostnader over fribeløp
        // AB-test: skattytere som har tallet 2 eller 6 i 7.siffer i partsnummeret
        // skal ikke gi utslag hvis Reisefradraget ikke har reell skattemessig virkning
        skattemelding
            .fokusområde("Fokusområde")
            .forForekomsterAv(reiserIArbeidsforhold)
            .utslagForFørste {
                val reiseForekomster = forForekomsterAv(arbeidsreise)

                val sumAntallReiser = reiseForekomster
                   .summer { antallReiser }

                val sumAntallKM = reiseForekomster
                   .summer { lengdeAvReiseTurRetur }

                val sumBomUtgifter = reiseForekomster
                   .summer { bompengeutgift.beloepSomTall }

                val sumFergeUtgifter = reiseForekomster
                   .summer { fergeutgift.beloepSomTall }

                val sumAntallKMOgBomUtgifter = sumBomUtgifter + sumFergeUtgifter

                hvis( (partsnr.tegnFraIndeks(7).finnesBlant("2", "6")) og 
                      (sumReisefradrag erStørreEnn 0) og 
                      ((sumAntallReiser erStørreEnnEllerLik 100) eller
                       (sumAntallKM erStørreEnnEllerLik 250)) og
                       (sumAntallKMOgBomUtgifter erStørreEnn 3_300)
                    ) 
              {
                    betjeningsstrategiSettesTil(ikke, merknadStandard)
                }
            }
    }

